#define GOLDEN_RATIO_ROT mat2(-0.7373688781, -0.6754902943, 0.6754902943, -0.7373688781)

vec4 bokeh(vec2 uv, sampler2D tex, float rad, float ratio, float samples, float gamma, float r)
{
    float mrad = sqrt(samples);
    vec4 accumulate = vec4(0);
    vec2 p = vec2(0, 1)*mat2(cos(r), -sin(r), sin(r), cos(r));
    for(float i = 0.; i < samples; i++)
    {
        accumulate += pow(texture(tex, uv + p*vec2(rad*ratio, rad)*sqrt(i)/mrad), vec4(gamma));
        p *= GOLDEN_RATIO_ROT;
    }
    return pow(accumulate / samples, 1./vec4(gamma));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(bokeh(uv, iChannel0, 0.02, iResolution.y/iResolution.x, 16., 3., float(iFrame)*2.39996322972865332).rgb,1.0);
}
