// inspired from Sig'23 "Iterative ð›¼-(de)Blending" https://ggx-research.github.io/publication/2023/05/10/publication-iadb.html
// Fork of https://shadertoy.com/view/mdX3Wl

#define blend(d)  O = mix( O, vec4(1), smoothstep( 2./R.y, 0., abs(d) ) )

void mainImage( out vec4 O, vec2 u )
{
 // O = T(u);                        // O.xy = UV coords
    O = texture(iChannel0, u/R, 2.); // averaged version

    vec2  P = 2.*O.xy - 1., d, C;    // --- draw UV map --------------------------------
    float l = length(P), a = atan(P.y,P.x);             // polar coords    
    O = 
 //     iMouse.z > 0. ? // texture(iChannel1, O.xy)     // map texture
 //                        sin(32.* vec4(l,a/2.,0,0) )  // polar grid
 //                   :   
                           sin(128.*O);                 // show UV grid
                        // vec4(l,fract(a/6.28),0,0) ;  // UV in polar coords
                        // sin(32.* vec4(l,a/2.,0,0) ); // polar grid

    l = 1e9;                         // --- plot NÂ² UV dots ----------------------------
    int N = 15;
    for( int i = 0; i < N*N; i++ )                      // plot some UV coords.
        d = ( .5 + vec2(i%N,i/N) )/float(N),
        P = T( d * R ).xy * R - u,                      // offset to current pixel
        a = dot(P,P),            
        a < l ? l = a, C = d : C;                       // keep min distÂ² and corresponding Id
    O = mix( O, vec4(C,1,1), smoothstep( 5.5, 4., sqrt(l) ) ); // draw dots

    d = c - u/R;                     // --- draw source & target shapes --------               
    blend( length( d )  - r );
    d = abs(r - u/R);
    blend( max(d.x,d.y) - r );
}

// inspired from Sig'23 "Iterative ð›¼-(de)Blending" https://ggx-research.github.io/publication/2023/05/10/publication-iadb.html
// Shapes can have any topological genus and dimension. Can be superimposed.
// Weighting on shapes can be consider (tune the sampler accordingly).

// shape parameters c,r in Common :   disc( (c,c), r ) , square [0,2r]Â²

#define hash2(p) fract(sin(mod((p)*mat2(127.1,311.7, 269.5,183.3),6.283)) *3758.5453123)

void mainImage( out vec4 O, vec2 u )
{
    if (iFrame==0) O = vec4(2.*r *u/R,0,0);              // init: P maps the square
    else {
        O = T(u);                                        // previous state
        float dt = 1./600., t = float(iFrame)*dt;
        if ( t > 1. ) return;                            // end of iterations

         vec2 P = O.xy,                                  // P: previous pivot
              H,
              A,                                         // point in the source shape ( square )
              B;                                         // point in the target shape ( disc )
        bool ok = false;
                                                         // ------- deblending phase --------        
        for( float i=0.; i++ < 6e2 && ok == false ; ) {  // rejection sampling
            H = hash2( P +t -i/1.3 +u/R );               // random vec2
            if ( t < .5 )                                // --- if first half of iterations:
                B = c + r * sqrt(H.x) * cos( 6.283*H.y + vec2(0,1.57)), // B sampled in disc
                A = ( P - t*B ) / (1.-t),                // A such that (1-t)A + tB = P
                H = abs(A-r), 
                ok = max(H.x,H.y) < r;                   // reject up to A in square

            else                                         // --- if second half if iterations: 
                A = 2.*r*H,                              // A sampled in square
                B = ( P - (1.-t)*A ) / t,                // B such that (1-t)A + tB = P   
                ok = length(B-c) < r;                    // reject up to B is disc
       }
                                                         // ------- blending phase --------
       if (ok) O.xy = mix(A,B,t+dt);                     // new pivot between A and B
    }
}
