const int K_Z = 90;

const int K_SHIFT = 16;
const int K_CTRL = 17;
const int K_ALT = 18;

#define ID(c) (distance(C, c)<1.)
#define getState(k)    ((texelFetch( iChannel0, ivec2(k, 0), 0 ).x)>.5)
#define getKeypress(k) ((texelFetch( iChannel0, ivec2(k, 1), 0 ).x)>.5)
#define getToggle(k)   ((texelFetch( iChannel0, ivec2(k, 2), 0 ).x)>.5)

void mainImage( out vec4 O, in vec2 C ){

    if(iFrame < 2 ){O = vec4(0);return;}

    vec4 last = texelFetch(iChannel1, ivec2(C), 0);

    if(ID(C_MOUSE)){O = (sign(iMouse.z)>.0) ? iMouse : last;return;}

    if(ID(C_CAMERA)){
        O = last;
        bool isFreeCamera = bool(last.y);
        if((getKeypress(K_NUM_0)||getKeypress(K_PAD_0)) && sign(iMouse.z)<=.0)O = vec4(0, 0, 0, 0);
        if(getState(K_NUM_1)||getState(K_PAD_1))O = vec4(1, true, 0, 0);
        if(getState(K_NUM_3)||getState(K_PAD_3))O = vec4(3, true, 0, 0);
        if(getState(K_NUM_7)||getState(K_PAD_7))O = vec4(7, true, 0, 0);
        return;}

    if(ID(C_CAM_SCALE)){
        if(getState(K_W))last-=.01;
        if(getState(K_S))last+=.01;
        O = last;
        return;}

    if(ID(C_TARGET_X)){
        if(getState(K_X) &&  getState(K_ALT))last+=.01;
        if(getState(K_X) && !getState(K_ALT))last-=.01;
        O = last;
        return;}

    if(ID(C_TARGET_Y)){
        if(getState(K_Y) &&  getState(K_ALT))last+=.01;
        if(getState(K_Y) && !getState(K_ALT))last-=.01;
        O = last;
        return;}

    if(ID(C_TARGET_Z)){
        if(getState(K_Z) &&  getState(K_ALT))last+=.01;
        if(getState(K_Z) && !getState(K_ALT))last-=.01;
        O = last;
        return;}
    if(ID(C_QUAD_VIEW))   {O = vec4(getToggle(K_Q));return;}
    if(ID(C_TURN_TABLE))  {O = vec4(getToggle(K_T));return;}
    if(ID(C_DEBUG_ISO))   {O = vec4(getToggle(K_I));return;}
    if(ID(C_DEBUG_COST))  {O = vec4(getToggle(K_C));return;}
    if(ID(C_DEBUG_MATCAP)){O = vec4(getToggle(K_M));return;}
    if(ID(C_DEBUG_NORMAL)){O = vec4(getToggle(K_N));return;}

    // blank pixel
    O = vec4(0,0,1,1);
}
