#define PI 3.14159
#define S(a,b,c) smoothstep(a,b,c)
#define SCALE .75
#define START 11.
#define ROT mat3(cos(.7), 0., sin(.7), 0., 1., 0., -sin(.7), 0., cos(.7))
#define ROT2 ROT*mat3(1., 0., 0., 0., cos(7.), sin(.7), 0., -sin(.7), cos(.7))
#define ROT3 ROT2*mat3(cos(.7), sin(.7), 0., -sin(.7), cos(.7), 0., 0., 0., 1.)


#define GYROFBM
const int MAX_MARCHING_STEPS = 120;

vec3 hash33(vec3 p){ 
    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768)*n); 
}

float gyronoise(vec3 p){
    return abs(dot(cos(p), sin(p.zxy)));
}

#ifdef GYROFBM
float fbm(vec3 p){

    float A = 1.;
    float n = 0.;
    float k = .6;
    float f = 1.;

    // Nimitz gyroid noise: https://www.shadertoy.com/view/3l23Rh
    for (int i=0; i<5; i++){
        p += .1*sin(p.xzy*.75*f+iTime/4.);
        n += gyronoise(p)*A-k; 
        A *= .7;
        p *= 1.95*ROT3;
        k -= .15;
        f *= 3.;
    }
    return n*1.2;
}
#else
float fbm(vec3 p){
    return (noise(p)-.1)*.5 + noise(p*ROT*2.)*.5 + noise(p*ROT*8.)*.25 + noise(p*ROT*16.)*.25 + noise(p*ROT*32.)*.25-.1;
}
#endif


float fbm2(vec3 p){
    return noise(p) + noise(p*ROT*2.)*.5;
}

float sdGyroid(vec3 p, vec3 offset, float scale, float thickness){
    p += offset;
    p *= scale;
    return abs(dot(cos(p), sin(p.zxy)))/scale - thickness;
}

float getGyr(vec3 p){
    float gyr = sdGyroid(p, vec3(10.,10.5,iTime/10.), SCALE, 1.5);
    return gyr;
}


vec2 map(vec3 p)
{
  float gyr = getGyr(p);
  return vec2(abs(gyr)*fbm(p+iTime/10.), gyr);//*S(2.5, 0., length(p)));
}

float flicker(){
    return pow(noise1(iTime*4.), 8.)*.75+.25;
}

void rayMarch(vec3 ro, vec3 rd, out vec4 col) {  

  //Vol March in Fog
  float t = START; // Starting point
  float fogT0 = 0.;

  vec3 lp = vec3(0.,0.,2.);
  vec3 ld; ld-=ld;

  float thresh= .35;
  float lDe, w;
  float td = 0.;
  float dit = fract(sin(dot(rd+ro, vec3(14.456, 17.2154, 73.156))*45678.123))*.0;
  float dt = 0.;
  for (int i = 0; i < MAX_MARCHING_STEPS; i++){

      if (td > .99) break;

      vec3 p = ro + (t+dit)*rd;
      vec2 d = map(p);

      vec3 ncol = vec3(0.);

      float f = flicker();
      float df = S(START*1.1-2.*f, START*1.5-2.5*f, length(p-ro)); //Dist flick
      vec3 flick_col = vec3(0.0,0.30,1.)*f*df;

      // Inspire by Shane's Cheap Cloud Flytrhough:  https://www.shadertoy.com/view/Xsc3R4
      // And Protean clouds by Nimitz : https://www.shadertoy.com/view/3l23Rh
      // Not as accurate as theirs but i will keep trying to improve the rendering and the fog texture
      if (d.x>thresh){
          float den = pow(d.x-thresh, 1.);
          td += den;

          ld = lp-p;
          float lDist = length(ld);
          ld = normalize(ld);

          vec3 sn = -rd + .1*hash33(p);
          float atten = 1./(1.+.125*pow(den, 2.) + .125*den + .25*length(p-ro));
          float interp = max(abs(getGyr(p)/1.3) -.45, 0.);
          float diff = max(dot(sn, ld), 0.);
          vec3 interior = mix(vec3(0.9,0.000,0.000), vec3(0.961,0.475,0.000), fbm2(p*8.)*.7);
          vec3 exterior = mix(vec3(.1, .05, .05), flick_col+interior*.5, df*.5);
          ncol = mix(exterior*(diff*.5+.25), interior*1.5, interp)*atten*(1.+den);
          col.rgb += ncol*(1.-td);
      }
      else{
          col.rgb += (vec3(1.)+flick_col)*dt*.03;
          td += .005;
      }
      dt = (.07-.06*S(thresh-.1, thresh, d.x))+fract(sin(dot(p, vec3(14.456, 17.2154, 73.156))*45678.123))*.0;

      t += dt;
  }
}


mat2 rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec4 render(vec3 ro, vec3 rd){
  vec3 backgroundColor = vec3(0.1, .1, .1);
  vec4 col = vec4(0.);
  rayMarch(ro, rd, col); 
  return col;
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos); // camera direction
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
	vec3 cu = normalize(cross(cd, cr)); // camera up
	return mat3(-cr, cu, -cd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

  vec3 ro = vec3(0, 0, -4); 
  vec2 m = iMouse.xy/iResolution.xy;
  vec3 lookat = vec3(0., 0.,0. );
  ro.yz = ro.yz * 1. * rot(mix(PI/2., 0., m.y));
  ro.xz = ro.xz * rot(mix(-PI, PI, m.x)) + vec2(lookat.x, lookat.z);
  vec3 rd = camera(ro, lookat)*normalize(vec3(uv, -1));

  vec4 col = render(ro, rd);

  vec3 background = vec3(.1,.1, .1);
    // Output to screen
  fragColor = vec4(pow(vec3(col), vec3(1.5)),1.0);
}

float noise(vec3 p)
{
	const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p);
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
	p -= ip; 
    p = p*p*(3. - 2.*p);
    h = mix(fract(sin(h)*43758.5453), fract(sin(h + s.x)*43758.5453), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}


float hash11(float p){
    return fract(sin(p*12.789)*7896.4563);
}

float noise1(float p)
{
    float i = floor(p);
    float f = fract(p);
    return mix(hash11(i), hash11(i+1.), smoothstep(0., 1., f));
}
