void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Convert pixel coordinates to a normalized range (-1 to 1)
    vec2 uv = (2.0*fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);

    // Create a time-based variable for animation
    float time = iTime * 0.5;

    // Generate a magical pattern
    vec3 color = 0.5 + 0.5 * cos(time + uv.xyx + vec3(0, 2, 4));

    // Add a glowing orb of light in the center
    vec2 center = vec2(0.0, 0.0);
    float orbSize = 0.1;
    float orb = smoothstep(orbSize, orbSize + 0.05, length(uv - center) + 0.1 * sin(time));
    color += vec3(1.0, 0.8, 0.2) * orb;

    // Add swirling trails of light
    float swirl = sin(time + length(uv - center) * 10.0);
    color *= vec3(0.5 + 0.5 * abs(swirl));

    fragColor = vec4(color, 1.0);
}
