#define pi 3.14159
#define thc(a,b) tanh(a*cos(b))/tanh(a)

#define blue1 vec3(126,229,218)/255.
#define blue2 vec3(82,163,204)/255.
#define blue3 vec3(46,109,153)/255.
#define blue4 vec3(46,57,115)/255.
#define brown0 vec3(217,217,195)/255.
#define brown1 vec3(193,182,162)/255.
#define brown2 vec3(161,136,124)/255.
#define brown3 vec3(132,92,92)/255.
#define brown4 vec3(91,70,84)/255.
#define brown5 vec3(66,57,66)/255.

#define rn 64.

// https://www.shadertoy.com/view/4tGXzt
float getFreq(float x) {
	return texture(iChannel0, vec2(floor(x * rn + 1.) / rn, 0)).x;
}

float getFreq_smooth(float x) {
	float index = floor(x * rn) / rn;
    float next = floor(x * rn + 1.0) / rn;
	return mix(getFreq(index), getFreq(next), smoothstep(0.0, 1.0, fract(x * rn)));
}

// Globals
float w, w2, h;

// x,y bottom left corner
float box(vec2 f, float x, float y, float width, float height) {
    return step(x, f.x) * step(f.x, x + width - 1.) *
           step(y, f.y) * step(f.y, y + height - 1.);
}

// x,y bottom left corner, rhombus
float rhom(vec2 f, float x, float y, float width, float height, float s) {
    f -= vec2(x + 1.,y);
    return step(0., f.y) * step(f.y, height) *
           step(f.x - width, s*f.y) * 
           step(s*f.y, f.x);
}

vec3 face(vec2 f, vec3 col) {
    f.x = abs(f.x-0.5);
    return mix(col, brown3, 
               step(f.x, 2.*w+h-f.y) * step(abs(f.x), w) * 
               step(0., f.y));
}

vec3 roof(vec2 f, vec3 col) {
    return mix(col, blue1, rhom(f, w, w+h, w2, w+1., -1.));    
}

vec3 trim(vec2 f, vec3 col) {
    col = mix(col, blue2, rhom(f, w, w+h, 0., w+1., -1.));
    col = mix(col, blue3, rhom(f, -w-3., w+h, 0., w+1., 1.));
    col = mix(col, blue4, rhom(f, -w-2., w+h, 1., w, 1.));
    col = mix(col, brown4, rhom(f, -w, w+h, 0., w-2., 1.) +
                           rhom(f, w-2., w+h, 1., w-1., -1.));   
    return col;
}

vec3 side(vec2 f, vec3 col) {
    col = mix(col, brown1, box(f,w+1.,0.,w2-2.,w+h));
    col = mix(col, brown2, box(f,w+1.,w+h-2.,w2-2.,2.));
    return col;
}

vec3 windowSide(vec2 f, vec3 col, vec3 win) {
    float w3 = floor(w2/3.); // sloppy
    col = mix(col, mix(win,blue2,.5), box(f, w+w3+2., 5., 3., 4.));
    col = mix(col, mix(win,blue3,.5), box(f, w+w3+2., 9., 3., 1.));
    col = mix(col, brown3, box(f, w+w3+5., 9., 1., 1.));
    col = mix(col, brown2, box(f, w+w3+5., 5., 1., 4.) +
                           box(f, w+w3+1., 3., 5., 1.) +
                           box(f, w+w3+1., 4., 1., 1.));
    return col;
}

vec3 windowTop(vec2 f, vec3 col, vec3 win) {
    col = mix(col, mix(win,blue3,.5), 
              box(f,-1.,12.,4.,4.) * 
              max(step(0.,f.x)   * step(f.x, 1.),
                  step(13., f.y) * step(f.y, 14.)));
    col = mix(col, mix(win,blue4,.5), box(f, 0., 15., 2., 1.) +
                                      box(f, -1., 14., 1., 1.));
    return col;
}

// should have written all objects like this 
// (centre at 0,0 then can translate arbitrarily)
vec3 windowFace(vec2 f, vec3 col, vec3 win) {
    col = mix(col, mix(win,blue2,.5), box(f, 1., 0., 2., 4.));
    col = mix(col, mix(win,blue3,.5), box(f, 1., 4., 2., 1.));
    col = mix(col, brown2, box(f, 0., 0., 1., 4.) +
                           box(f, 2., -1., 1., 1.));
    col = mix(col, brown4, box(f, -1., -2., 4., 1.));
    return col;
}

vec3 windowsFace(vec2 f, vec3 col, vec3 win) {
    col = windowFace(f - vec2(5,5), col, win);
    col = windowFace(f - vec2(-6,5), col, win);
    return col;
}

vec3 door(vec2 f, vec3 col) {
    col = mix(col, brown4, box(f, 0., 0., 3., 9.));
    col = mix(col, brown5, box(f, 0., 9., 3., 1.));
    col = mix(col, brown2, box(f, -1., 0., 1., 9.));
    return col;
}

vec3 chimney(vec2 f, vec3 col) {
    col = mix(col, brown1, box(f, w+5., w+h+5., 4., 10.));
    col = mix(col, brown3, step(2.*w+h+2.*5.-1.-f.y, f.x) *
                           box(f, w + 1., w+h+5., 4., 10.));   
    col = mix(col, brown0, box(f, w+5., w+h+14., 4., 1.));
    col = mix(col, brown2, box(f, w+1., w+h+14., 4., 1.));
    col = mix(col, blue2, rhom(f, w-2., w+h+5., 4., 3., -1.));
    col = mix(col, brown4, box(f, w+1., w+h+11.,2.,1.) +
                           box(f, w+4., w+h+11.,1.,1.));
    col = mix(col, brown2, box(f, w+5., w+h+11.,1.,1.) +
                           box(f, w+7., w+h+11.,2.,1.) +
                           box(f, w+6., w+h+9.,2.,1.));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(130,161,124)/255.;
    float t = iTime;
    vec2 res = iResolution.xy;   
    vec2 uv = (fragCoord - 0.5 * res) / res.y;
    float sc = 52.;
    vec2 f = round(sc * uv) - 0.;
    f.x += 7.;
    f.y += floor(0.5*sc);  

    float fr = getFreq_smooth(0.);

    // song is 126 BPM, hacky way of matching it (probably incorrect!)
    float t2 = 0.5*pi*125.9/60.*t;
    float th = step(9.*pi, t2); //round(8.*tanh(0.1 * max(0., t-8.))) / 8.;
    float i1 = round(th*(5. + 5. * thc(10., t2)));
    float i2 = round(th*(5. + 5. * thc(10., t2 + 0.5*pi)));

    w = 9. + i1;
    h = 15. + i2 - w;

    // roof width
    w2 = 15. + i2 + 3. + 2.;

    vec3 win = .6 + .4 * cos(2.*pi*(uv.y/5.+.1*t+2.*fr+vec3(0,1,2)/3.));

    col = face(f, col);
    col = roof(f, col);
    col = trim(f, col);
    col = side(f, col);
    col = windowSide(f, col, win);
    col = windowTop(f, col, win);
    col = windowsFace(f, col, win);    
    col = door(f, col);
    col = chimney(f, col);

    fragColor = vec4(col,1.0);
}
