#define H(p) fract ( 1e4*sin( dot( p , vec2(71,-97) )) )           // rand
#define B(s) length( F - .707*vec2( .5*s , fract(d*iTime) -.5) )   // ball SDF
#define D(v) max( 1. - v * R.y/20., 0.)                            // AA draw

void C( vec2 U, vec2 D, inout float l, inout float x ) {
    vec2 I = floor(U)      + D,              // cell Id
         F = fract(U) - .5 - D;              // cell coordinates

    float s = sign(H(I)-.5),                 // wall direction ( one of the 2 cell diagonals )
          d = mod(I.x+I.y,2.) > 0. ? s : -s; // balls direction
    F = ( F + s*vec2(-F.y,F) ) *.707;        // wall frame
    x = min( x, abs(F.x) );                  // min distance to wall

    d *= sign( F.x );
    l = min(l, min( B(s), B(-s) ) -.1 );     // min distance to balls on each wall side
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 10. * u / R.y;

    float x=9., l=9.;
    C( U, vec2(0)    , l,x );                 // distance to balls and walls
    C( U, vec2( 1,0) , l,x );                 // check neighbors when overlapping
    C( U, vec2( 0,1) , l,x );
    C( U, vec2(-1,0) , l,x );
    C( U, vec2(0,-1) , l,x );

    O *= 0.;
    O   += D(x) * ( .5 + .5*sin(iTime) );     // draw walls
    O.r += D(l);                              // draw balls


}
