#define pi 3.14159
#define dir(a) vec2(cos(a),sin(a))
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

#define black vec3(0)
#define pink vec3(236, 192, 209) / 255.
#define blue vec3(133, 192, 234) / 255.

// Set color = draw color for pixels inside box
#define b(X,Y,W,H) if (X<=f.x&&f.x<X+W&&Y<=f.y&&f.y<Y+H) \
                       col = vec4(draw, 1);

vec4 drawPiggy(ivec2 f, int x, int y) {
    vec4 col;  // Output color
    vec3 draw; // Draw color

    f -= ivec2(x, y);
    f.x = abs(f.x);

    draw = pink;
    b(0, -1, 7, 9);
    b(0, -6, 6, 12);

    draw = black;
    b(1, 0, 1, 1);
    b(0, 2, 3, 1);
    b(0, -2, 3, 1);
    b(3, -1, 1, 3);
    b(3, 3, 2, 2);
    b(3, 6, 2, 1);
    b(5, 5, 2, 1);
    b(6, 4, 1, 1);
    b(0, 7, 4, 1);
    b(4, 8, 3, 1);
    b(7, 6, 1, 2);
    b(7, -1, 1, 5);
    b(6, -3, 1, 2);
    b(5, -7, 1, 4);       
    b(2, -7, 4,  1);
    b(0, -6, 3,  1);
    b(3, -5, 1,  1);

    return col;
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         u = (U+U-R)/R.y;

    // Background
    O.rgb = blue;        
    O *= .95+.05*smoothstep(-.01, 0.01, .25-abs(fract(.75*(u.x-u.y)+.1*iTime)-.5));

    // Little piggies
    for (float i; i++ < 10.;) {
        float io = 2. * pi * i / 10.;
        vec2 v = fract(u + iTime * (.2 + .02*i*dir(io))) - .5;
        ivec2 f = ivec2(floor(77. * v));
        vec4 pig = drawPiggy(f, 0, 0);
        O = mix(O, pig, .1 * pig.a);
    }

    // Big piggy
    u *= rot(pi/32.*cos(iTime));
    u += .13 * dir(-iTime);
    ivec2 f = ivec2(floor(11.*u));
    vec4 pig = drawPiggy(f, 0, -2);
    O = mix(O, pig, pig.a);

    // Vignette
    float xp = exp(.1 - .12 * dot(u,u));
    O.rgb = xp * mix(pink, O.rgb, xp);
}
