#define MAX_STEPS 100
#define MAX_DIST 255.
#define MIN_DIST 0.001
#define EPSILON 0.001
#define Ka 0.5
#define Kd 0.4
vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material);
vec3 getMaterial( vec3 pp, float id_material);
vec3 light_pos1;  vec3 light_color1 ;
vec3 light_pos2;  vec3 light_color2 ;


#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float sdCylinderYZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.yz),p.x)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xz),p.y)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXY( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xy),p.z)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }


float Intersect(float distA, float distB)
	{ return max(distA, distB);}
float Union(float distA, float distB)
	{ return min(distA, distB);}
float Difference(float distA, float distB)
	{ return max(distA, -distB);}

float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}

vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdTrapezoid( in vec2 p, in float r1, float r2, float he )
{    vec2 k1 = vec2(r2,he);
    vec2 k2 = vec2(r2-r1,2.0*he);
    p.x = abs(p.x);
    vec2 ca = vec2(p.x-min(p.x,(p.y<0.0)?r1:r2), abs(p.y)-he);
    vec2 cb = p - k1 + k2*clamp( dot(k1-p,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

vec2 columna(vec3 p, vec2 res)
{   vec3 p0=p;
    p.x=abs(p.x)-3.0;
    p.z=abs(p.z)-3.0;
    float d1= sdCylinderXZ(p,vec2(2.0,10.0) );
    p=p0; p.y=abs(p.y)-7.0;
    float d2=sdBox(p, vec3(6.0,0.5,6.0) );
    p=p0; p.y=abs(p.y)-9.5;
    float d3=sdBox(p, vec3(6.0,0.5,6.0) );
    res =opU2(res, vec2(d1,100.0));
    res =opU2(res, vec2(d2,100.0));
    res =opU2(res, vec2(d3,100.0));
    return res;    
}

vec2 puertaA(vec3 p,vec2 res)
{
    vec3 p0=p;
    float d1=sdBox( p-vec3(0.0,-2.0,0.0), vec3(8.0,8.0,1.0) ); //bloque grande
    p.y+=4.0;
    float d2=sdBox( p, vec3(5.0,8.0,1.5) );  //puerta
    float d2a=sdBox( p-vec3(-6.5,0.0,0.0), vec3(0.5,8.0,2.0) );  //puerta
    float d2b=sdBox( p-vec3(6.5,0.0,0.0), vec3(0.5,8.0,2.0) );  //puerta
    float df= Difference(d1, d2);    
    float d3a= sdTrapezoid(p.xy-vec2(0.0,12.0), 7.0, 0.25, 3.0 );
    float d3b1=sdBox( p-vec3(0.0,10.0,0.0), vec3(8.0,12.0,1.0) ); 
    float d3b2=sdBox( p-vec3(0.0,10.0,0.0), vec3(8.0,12.0,1.5) ); 
    float d3c= sdTrapezoid(p.xy-vec2(0.0,11.0), 6.0, 0.25, 2.5 );
    d3a= Intersect(d3a, d3b1);
    d3c= Intersect(d3c, d3b2);
    res =opU2(res, vec2(df,100.0));
    res =opU2(res, vec2(d3a,100.0));
    res =opU2(res, vec2(d3c,100.0));
    res =opU2(res, vec2(d2a,100.0));
    res =opU2(res, vec2(d2b,100.0));
    return res;
}

vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  
     p.y=p.y+10.0;
    vec3 p0=p;
    float planeDist1 = p.y+10.0;  //piso inf
    res =opU2(res, vec2(planeDist1,100.0));
    vec3 pa=p;
    vec3 pb=p;
    pa.x=opRep1D(pa.x, 40.0 );
    pb.x=opRep1D(pb.x+20.0, 40.0 );
    res=columna(pa,res);
    res=puertaA(pb,res);
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}


float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 pallete(float t) {
    vec3 a = vec3(0.2);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.0, 0.33, 0.67);
    return a + b * cos(6.28318 * (c*t + d));
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = p;
     vec3 V = normalize(ro - p);
    vec3 N = normal;
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);    
    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);
    float shadow = getSoftShadow(worldPos, L);// shadows
    float occ = occlusion(worldPos, N);// occ
    vec3 ambient = Ka + Ka * dot(normal, vec3(0., 1., 0.))*lightColor;
    ambient*=0.5;
    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff= clamp(dot(N, L), 0.0, 1.0);
    vec3 diffuse =  lightColor * diff;
    float shininess=10.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);
    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.25 +back) + vec3(.5)*specular*specular;
    return colOut;
}


vec3 Getluz(vec3 ro,vec3 p, vec3 lp, vec3 lcol,vec3 n, float diffp, vec3 specp)
{    vec3 v=normalize(ro-p);
    vec3 h=normalize(lp+v);
    vec3 l=normalize(lp-p);
    float ndh=max(0.0,dot(n,h));
    float ndl=max(0.0,dot(n,l));
    float ndv=max(0.0,dot(n,v));   
    float diff=ndl*diffp;
    vec3  spec=0.8*pow(ndl,10.0)*specp;
    vec3 col=(diff+spec*lcol);
    return col;

}


vec3 getColorTextura( vec3 p, vec3 nor,  int i)
{	vec3 col;
    if (i==100 )
    { col=tex3D(iChannel0, p/32., nor); return col*2.0; }
	if (i==101 ) { return tex3D(iChannel1, p/32., nor); }
	if (i==102 ) { return tex3D(iChannel2, p/32., nor); }
	if (i==103 ) { return tex3D(iChannel3, p/32., nor); }
    return vec3(0.0);
}


float N21 (vec2 p){
    return fract( sin(p.x *  100. + p.y * 6574.) * 5647.);
}

float SmoothNoise(vec2 uv){
    vec2 lv = smoothstep(0., 1., fract(uv));
    vec2 id = floor(uv);
    float bl = N21(id);
    float br = N21(id + vec2(1,0));
    float b = mix(bl, br, lv.x);

    float tl = N21(id + vec2(0, 1));
    float tr = N21(id + vec2(1, 1));
    float t = mix(tl, tr, lv.x);
    return mix(b, t, lv.y);
}

float smoothNoise2(vec2 uv){ 
    float c = SmoothNoise(uv*4.);
    c += SmoothNoise(uv*8.)* 0.5;
    c += SmoothNoise(uv*16.) * 0.25;
    c += SmoothNoise(uv*32.) * 0.125;
    c += SmoothNoise(uv*64.) * 0.0625;
    return c/2.;
}

///https://www.shadertoy.com/view/dttcDN
vec3 nubes(vec2 uv )
{float c = smoothNoise2(uv) + 0.2 + 0.4*sin(iTime);
    vec3 col = vec3(c/1.5, c/1.2, c);
    return col;
}

vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col+nubes(rd.xy*3.0), vec3(.3, .6, .8), 1.0 - rd.y);
    return sky;
}

//https://www.shadertoy.com/view/WdsGR7
vec3 getColor(float v)
{  float r = cos((v-0.78)*PI*1.66);
   float g = cos((v-0.58)*PI*1.66);
   float b = cos((v-0.28)*PI*1.66);
   return vec3(r,g,b); 
}

vec3 GetMaterial(vec3 p,  vec3 nor, vec3 ro,  vec3 rd, int id_color)
{  vec3 colobj; 
   if (id_color<40) { colobj=getColor(int( id_color));  return colobj; }
   if (id_color==66) {return  .5 * texture( iChannel0, 0.6*p.xz).yyy; }
   if (id_color>=100 ) { return  getColorTextura( p, nor,id_color); }   
  return vec3(0.0);
}

vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }

vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43;d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
  vec3 p;
     vec2 hit=RayMarch(ro,rd, MAX_STEPS);
      if(hit.x<MAX_DIST)
       {   p = (ro + rd * hit.x );
        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y));
        vec3 result;

        result= lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2;

        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;
    }
    else if(hit.x>MAX_DIST)
    col= render_sky_color(rd);
   return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x = uv.x * iResolution.x / iResolution.y;
    vec2 mouseUV = iMouse.xy/iResolution.xy; // Range: <0, 1>   
    float t0=iTime;
    float t=mod(t0*5.0,500.0);
 	light_pos1= vec3(-10.0, 120.0, -25.); light_color1=vec3( 1.0,1.0,1.0 );
 	light_pos2= vec3(10.0, 20.0, -25.0 ); light_color2 =vec3( 1.0,1.0,1.0 ); 

   vec3 ro=vec3(0.0,-12.0,-20.0);
   float px=t0*5.0;
   ro.x+=px;

   vec3 rd=normalize( vec3(uv.x,uv.y,1.0));
   if(3.0*sin(t0)>0.0)
      ro=vec3(px,-12.0,-25.0);
   else
      rd= rotate_y(rd, radians(45.0));
    vec3 col= Render( ro,  rd);
    fragColor = vec4(col,1.0);
}
