// interactive variant of https://shadertoy.com/view/cdfGz7

void mainImage( out vec4 O, vec2 u )  { 
    O = sqrt(T(u));
 // O = sqrt(1.-exp(-T(u)*vec4(4,2,1,1)));                    // color

 // O = vec4( .5+.5*field(u/iResolution.y,iChannel1), 0,1);   // debug
 // field(u/iResolution.y,iChannel1); O=C;                    // debug
 // O*=0.; for (float i=0.; i<Nvort;i++) O += .01/length(u/iResolution.y-texelFetch(iChannel1,ivec2(i,1),0).xy); // debug
}

// ---- compute BBoxs of next curves / manage interactive vortices

void mainImage( out vec4 O, vec2 u )
{
    R = iResolution.xy;
    vec2 M = iMouse.xy/R.y;
    if (u-.5==vec2(2)) {     // T(2,2).a = vortex closest to mouse ( if clicked )
        float l, m = R.x, v;
        for (float i=0.; i<Nvort; i++ ) {
            l = length( M - T(vec2(i,1)).xy );
            if (l < m ) m = l, v = i;
           // l < m ? m = l, v = i : v;
            }
        O.a = iMouse.z>0. ? v : -1.;
    } 

    if (u.y==1.5) {                                // --- manage vortices
        O.xy = iFrame<1 ?        H(u.x-.5)         // init vortex
             : u.x-.5==T(2).a ?  M                 // closest follow mouse ( if clicked )
         //  :                   T(u).xy;          // startionary
             :       mod( T(u).xy + (H(u.x)-.5)/1e3, R/R.y); // evolving
    }
    if (u.y==.5) {                                 // --- compute BBoxs of next curves
        float l = 9., p = 1./R.y;
        vec2  P = H(u.x-iTime), _P=P, m=P; M=P;    // random start point

        for( int t=0; t <len; t++, _P = P )        // follow trajectory
            P += field(P,iChannel0),
            m = min(m,P), M = max(M,P);        

        O = vec4(m-p,M+p);
    }
}

int     len = 20;  // curve length ( i.e. number of samples )
float Nvort = 30., // number of vortices 
      Nline = 80.;

vec2 R; // can't #define because used in field (via H) but Common is stupidely not an include

#define T(U)   texelFetch( iChannel0, ivec2(U), 0 )
#define H(p) ( fract(sin(mod(float(p)*vec2(269.5,183.3),6.283)) *1e4)*R/R.y )
#define hue(v)( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

float L(vec2 p, vec2 a,vec2 b) { // --- draw line  ( squared distance )
    p -= a, b -= a;
    float h = dot(p, b) / dot(b, b), 
          c = clamp(h, 0., 1.);     
    return h==c ? dot(p -= b*h ,p) : 1e5; 
}

vec4 C = vec4(0);  // returns color field

vec2 field(vec2 U, sampler2D ch) {  // --- velocity field induced by vortices ( Biot & Savart )
    vec2 V = vec2(0), P;
    float w;
    for( float i=0.; i < Nvort; i++ ) { // N random vortices ( same strength )
        P = texelFetch(ch,ivec2(i,1),0).xy - U; // don't use ',' or bug on Windows !
        w = min( 1., .001 / pow(length(P),3.) ), 
        C += hue(i/Nvort)    * w ,        // colored influence
        V += vec2(-P.y,P.x)  * w          // velocity influence
                 // * sign(H(i+.3).y-.5)  // random sign
                 // * (2.*H(i+.3).y-1.)   // random sign & strength
                 // * H(i+.3).y*2.        // random strength
                          // * min(100.,.2/dot(P,P) )
                ;}
    return V*.1;
}

// --- draw only curves which BBox covers the pixel

void mainImage( out vec4 O, vec2 u )
{
    R = iResolution.xy;
    vec2 U = u / R.y, P, _P, b;
    O =  .98*T(u);                         // cumulates previous draw

    for( float i=0.,l; i < Nline; i++ ) {  // draw N streamlines per frame
        vec4 B = texelFetch(iChannel1,ivec2(i,0),0); // curve bbox
        b = step(B.xy,U)*step(U,B.zw);
     // if (b.x*b.y == 0.) continue;       // â† this form crashes Firefox !
        if (b.x*b.y == 1.)
        {               // pixel not in BBox: skip draw
            _P = P = H(i+.5-iTime);             // random start point
            l = 9.;
            for( int t=0; t <len; t++, _P = P ) // follow trajectory
                P += field(P,iChannel1),
                l = min( l, L(U,_P,P) );
        O += .2*max(0., 1.-R.y*sqrt(l))         // draw the curve
             * ( .4 + C*.02 );                  // color field
       }
    }
}
