// @ENDESGA 2023
uint noise(uint x, uint y, uint z) {
    x = ~(~x - y - z) * ~(x - ~y - z) * ~(x - y - ~z);
    y = ~(~x - y - z) * ~(x - ~y - z) * ~(x - y - ~z);
    z = x ^ y ^ (~(~x - y - z) * ~(x - ~y - z) * ~(x - y - ~z));
    return z ^ ~(~z >> 16);
}

///////

void mainImage( out vec4 o, in vec2 i ) {
    i += iTime*100.;
    o = vec4(
        vec3(
            noise(
                uint(i.x),
                uint(i.y),
                uint(iTime)
            )
        )/float(0xffffffffu)
        ,1.);
}
