/**
 * @author rah, inspired by jonobr1 / http://jonobr1.com/
 */

/* 
 * Output circle colors at given center pos and radius
 */
vec4 circle(vec2 uv, vec2 center, float radius, vec3 color)
{
    vec2 p = (2.0 * uv-iResolution.xy);
    p = rotatePoint2d(p, vec2(0.0, 0.0), iTime);

    float a = atan(p.x,p.y);

    float radModifier1 = cos(a * 8.f) * 90.0f * bias(cos(iTime), 0.5);

    float radModifier2 = triangle(a, 0.3f, 0.8f) * 400.f * gain(sin(iTime), 0.8);

    float d = length(center - uv) - (radius - radModifier1 - radModifier2);
    float t = clamp(d, 0.0, 1.0);

    return vec4(color, 1.0 - t);

}

/*
 * Your work here!
 */
float computeRadius(vec2 uv)
{
    float radius = 0.25 * iResolution.y;

    // Centered uv
    vec2 uvCenter = (2.0f * uv - iResolution.xy);
    // Get pixel angle around the center
    float a = atan(uvCenter.x,uvCenter.y);

    return radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = vec2(iResolution.x * 0.4, iResolution.y * 0.5);
	float radius = computeRadius(uv);

    // Background layer
	vec4 layer1 = vec4(rgb(230.0, 230.0, 250.0), 1.0);

	// Circle
	vec3 red = rgb(255.f, 255.f, 255.f);
    vec3 black = rgb(0.f, 0.f, 0.f);
    vec4 layer2 = circle(uv, vec2(iResolution.x * 0.6, iResolution.y * 0.5), 
        radius, black);
	vec4 layer3 = circle(uv, center, radius, red);

	// Blend the two
	fragColor = mix(layer1, layer2, layer2.a);
    fragColor = mix(fragColor, layer3, layer3.a);

}

/*
 * Toolbox functions! ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 */ 
float bias(float t, float b) {
    return (t / ((((1.0/b) - 2.0)*(1.0 - t))+1.0));
}

float gain(float g, float t) {
    if (t < 0.5f) {
        return bias(1.f - g, 2.f * t) / 2.f;
    } else {
        return 1.f - bias(1.f - g, 2.f - 2.f * t) / 2.f;
    }
}

/*
 * Basic Utilities ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 */

float triangle(float x, float freq, float amp) {
    float modTerm = mod((x * freq), amp);
    float halfAmp = 0.5f * amp;
    return abs(modTerm - halfAmp);
}

/* 
 * Rotate an input point about some center by angle
 */
vec2 rotatePoint2d(vec2 uv, vec2 center, float angle)
{
    vec2 rotatedPoint = vec2(uv.x - center.x, uv.y - center.y);
    float newX = cos(angle) * rotatedPoint.x - sin(angle) * rotatedPoint.y;
    rotatedPoint.y = sin(angle) * rotatedPoint.x + cos(angle) * rotatedPoint.y;
    rotatedPoint.x = newX;
    return rotatedPoint;
}

/*
 * Make a vec3 color from rgb values [0, 255]
 */
vec3 rgb(float r, float g, float b)
{
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}
