vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    for(int k = 0; k < 6; k++){
        uv =
            abs(.5+uv+t2)/1.5
            //abs(.5*sign(t2.y-t2.x)+uv+t2)/1.5
        ;
        b = uv.y<uv.x;
        if(b) uv = uv.yx;
        t2 =
            -triangle_wave(uv+1.5)
        ;
        uv =
            t2-triangle_wave(uv.yx)
            //t2-triangle_wave(uv.yx+floor(.5+t2.y+t2.x))
        ;
        {t2 = t2.yx; uv=uv.yx;}
        col.x =
            //sign(uv.x-uv.y)*sign(t2.x-t2.y)
            max(sign(uv.x-t2.y)*sign(uv.y-t2.x),col.x)
            //max(col.x,sign(uv.x-uv.y)*sign(t2.x-t2.y))
            //max(col.x,float(b))
        ;
        col =
            abs(col.yzx-float(b)/2.)
            //abs(col.yzx*sign(uv.y-uv.x)-float(b)*sign(t2.x-t2.y))/2.
            //abs(max(col.yzx*sign(uv.x-uv.y),col*sign(t2.x-t2.y))-float(b)/2.)
            //min(col.yzx,abs(col.yzx-float(b)/2.))
        ;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(col,1.0);
}

//remix of https://www.shadertoy.com/view/mdKXz1

float fmod(float a, float b){
    return
        mod(floor(a),b)
    ;
}


vec2 mainSound(int samp, float time){
  float tempo = 8.;
  tempo *= pow(2.,fmod(time*tempo,3.));

  time /= tempo;
  float s1 =
      8.;

  float s2 = tempo,
  t=
      time*tempo
  ,
  m1 =
      fmod(t*8.,s2)
  ,
  m3 =
      (fmod(t,abs(s2*2.-m1)+1.)-m1);
  float m2 =
      1. + fmod(t,s1)
  ;

  float a=
      64.*(1.-sqrt(fract(t/s1)/1.25))*.2
  ,

  nb = time*tempo*pow(2.,floor((m3+m2)/2.)/5.+6.5);

  return
      abs(.5-vec2(fract(nb*.998*2.),fract(nb)))*a
  ;
}
