mat2 rot(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a));}
float hash21(vec2 p){return fract(sin(dot(p,vec2(27.619,57.583)))*43758.5453);}

// @Shane
// An offset grid pattern with some random subdivision.
vec4 distField(vec2 p, inout float sc){    
    vec2 q = p;
    vec2 ip = floor(p/sc);
    p -= (ip + .5)*sc;

    for(int i = 0; i<5; i++){
        if(hash21(ip + float(i + 1)*.01)<.235){
            sc /= 2.;
            p = q;
            ip = floor(p/sc);
            p -= (ip + .5)*sc;
        }
    }
    return vec4(p, ip);
}


void mainImage( out vec4 fragColor, in vec2 F ){

    vec2 uv = (F.xy - R.xy*.5)/R.y;
    vec2 vv = (2.*F.xy-R.xy)/max(R.x,R.y);

    float cale = .1591;
    if(vv.y<.333 && vv.y>-.333) {

        uv.x -= .25;
        uv /= uv.x*uv.x + uv.y*uv.y;
        uv.x += 2.;

        uv *= rot(-T*.05);
        uv = vec2(log(length(uv)), atan(uv.y, uv.x))*cale;
        uv.x -= T*.025;
    }else{
        uv.y-=.15*sin(uv.x*3.+T*.5);
        uv.x-= T*.05;
    }
    vec2 p = uv;
    //

    float oSc = 1./4.;
    float sc = oSc; 

    vec4 d4 = distField(p, sc);
    float px = fwidth(p.x)*.8;

    vec2 dv = d4.xy;
    vec2 id = d4.zw;   

    float ch = mod(id.x+id.y,2.)*2.-1.;

    vec3 col = vec3(.0);

    float rnd = hash21(id);
    float bnd = fract((rnd*147.32)+(T*.025));

    vec3 h  = .45 + .45*cos(4.5*(uv.y+(rnd*.5)) + vec3(0, 1, 2));

    if(rnd<.45) dv.x = -dv.x;
    rnd = fract(rnd*247.32);

    float sx = .5*sc;

    vec2 gx = length(dv-sx)<length(dv+sx) ? vec2(dv-sx) : vec2(dv+sx);
    float cx = length(gx)-sx;

    if(rnd>.85 && bnd<.4) cx = min(length(dv.x),length(dv.y))-(.001*sc);

    if (bnd<.25) { 
        cx = smoothstep(px,-px, abs(abs(abs(cx)-(.2*sc))-(.1*sc))-(.05*sc));
    } else if (bnd>.4) {
        cx = (ch>.5 ^^ rnd<.45) ? smoothstep(px,-px,cx):smoothstep(-px,px,cx);
    } else {
        cx = smoothstep(-px,px, abs(cx)-(.125*sc));
    }

    col = mix(h,col,cx);
    px = fwidth(vv.x);
    float bl = length(abs(vv.y)-.34)-.02;
    col = mix(col,vec3(.0),smoothstep(px,-px,bl));

    // text overlay
    vec4 O = vec4(0);
    vv.x += T*.25;
    vec2 dd = floor(vec2(vv.x,1)*scale);
    vv.x = fract(vv.x*scale)-.5;
    vv.y *= scale;



    float ph = .25;

    float ss = ph*sin(.2*dd.x*PI+T)+.25;
    vv =(vv*.25)+vec2(.48,ss);

    float ds=(26.*R.x/R.y);
    vv = floor(vv*ds)/ds;
    vec2 U = vv;
    px = 255./R.x;

    int mid = int(mod(floor(dd.x),187.));
    U = (vv)/.5;
    C(txt[mid]);

    col = mix(col,vec3(1),smoothstep(px,px+.9,O.x));

    fragColor = vec4(pow(col, vec3(.4545)),1.);
    }
