void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a_n_pixel_coord_norm = fragCoord.xy / iResolution.xy; 

    float n_screens = 3.0;
    fragColor = vec4(
        1.0
    );
    for(float n_screen = 0.0; n_screen < n_screens; n_screen+=1.0){
        float n_x_norm_screen = mod(a_n_pixel_coord_norm.x,1.0/n_screens) / (1.0/n_screens);

        if(a_n_pixel_coord_norm.x > 0.0*(1.0/n_screens)){

            fragColor = vec4(
                1.0,  // red is 1.0
                0.0, 
                0.0, 
                1.0
            );
        }

        if(a_n_pixel_coord_norm.x > 1.0*(1.0/n_screens)){
            fragColor.rgba = fragColor.bgra;
            //          ^ blue is from   ^ red (1.0)
            //
            // now blue holds 1.0
        }
        if(a_n_pixel_coord_norm.x > 2.0*(1.0/n_screens)){
            fragColor.rgba = fragColor.rbra;
            //         ^ green is from  ^ blue (1.0)
            // 
            // now green holds 1.0

        }


    }
}
