#define AA  0  // (= 0/1) optional antialiasing

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrCylDf (vec3 p, float r, float h);
float Minv3 (vec3 p);
float SmoothMax (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float Hashfv2 (vec2 p);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

struct GrParm {
  float gFac, hFac, fWav, aWav;
};
GrParm gr;

vec4 dateCur;
vec3 sunDir, qHit, rPos;
vec2 gId;
float tCur, dstFar, hgSize, sRad, sAng;
int idObj;
bool isOcc;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float SurfDf (vec3 p, float r)
{  // (intersection of torus and cylinder)
  float rt;
  rt = 1.1 * r;
  return max (abs (p.z) - rt, SmoothMax (abs (length (vec2 (length (vec2 (p.y, p.z - rt)) -
     3. * rt, p.x)) - 2. * rt) - 0.01, length (p.xy) - r, 0.01));
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  if (isOcc) {
    p.xz -= HexToPix (gId * hgSize);
    p -= rPos;
    q = p;
    q.y -= -1.;
    d = PrCylDf (q.xzy, 0.5, 2.);
    DMINQ (1);
    q.y -= 2.1 + sRad;
    d = PrCylDf (q.xzy, 0.03, sRad + 0.1);
    DMINQ (2);
    q.xz = Rot2D (q.xz, sAng);
    q.z += 0.2 * sRad;
    d = SurfDf (q, sRad);
    DMINQ (3);
  }
  return dMin;
}

float GrndHt (vec2 p)
{
  mat2 qRot;
  vec2 q;
  float f, wAmp;
  qRot = mat2 (0.8, -0.6, 0.6, 0.8) * gr.fWav;
  q = gr.gFac * p;
  wAmp = 4. * gr.hFac;
  f = 0.;
  for (int j = 0; j < 4; j ++) {
    f += wAmp * Noisefv2 (q);
    wAmp *= gr.aWav;
    q *= qRot;
  }
  return f;
}

void SetGConf ()
{
  float h;
  rPos.xz = 0.5 * hgSize * sin (2. * pi * Hashfv2 (17.1 * gId + 0.3) + vec2 (0.5 * pi, 0.));
  rPos.y = GrndHt (HexToPix (gId * hgSize) + rPos.xz);
  h = Hashfv2 (19.1 * gId + 0.3);
  isOcc = (abs (h - 0.5) < 0.45);
  sRad = 3. + 0.5 * step (0.25, abs (h - 0.5));
  sAng = (0.1 + 0.05 * h) * sign (2. * h - 1.) * tCur;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]), dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
  s = Minv3 (hv);
  gIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId != gIdP) {
      gIdP = gId;
      SetGConf ();
    }
    d = ObjDf (p);
    if (dHit + d < s) dHit += d;
    else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
    }
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + s * rd;
    h = p.y - GrndHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.5, 0.8 * h);
  }
  if (h < 0.) {
    sHi = s;
    for (int j = VAR_ZERO; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (p.y > GrndHt (p.xz)) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy),
     GrndHt (p.xz + e.yx)), e.x).xzy);
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec4 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e;
  float h;
  h = RippleHt (p);
  e = vec2 (0.002, 0.);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vec4 (vn, h);
}

vec3 SkyBg (vec3 rd)
{
  return mix (vec3 (0.2, 0.3, 0.7), vec3 (0.45, 0.45, 0.5), pow (1. - max (rd.y, 0.), 8.));
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  float sd, f;
  ro.x -= tCur;
  sd = max (dot (rd, sunDir), 0.);
  f = Fbm2 (0.05 * (ro + rd * (100. - ro.y) / (rd.y + 0.0001)).xz);
  return mix (SkyBg (rd) + vec3 (1., 1., 0.9) * (0.3 * pow (sd, 32.) + 0.2 * pow (sd, 512.)),
     vec3 (1., 1., 0.95) * (1. - 0.1 * smoothstep (0.8, 0.95, f)), clamp (0.9 * f * rd.y, 0., 1.));
}

vec3 ObjCol (vec3 ro, vec3 rd, float dstObj)
{
  vec3 col, vn;
  float dFac, nDotL;
  dFac = 1. - smoothstep (0.15, 0.35, dstObj / dstFar);
  ro += dstObj * rd;
  vn = ObjNf (ro);
  if (idObj == 1) {
    col = vec3 (0.7, 0.75, 0.8);
    vn = VaryNf (16. * qHit, vn, 2. * dFac);
  } else if (idObj == 2) {
    col = vec3 (0.8, 0.8, 0.9);
  } else if (idObj == 3) {
    col = vec3 (0.9, 0.9, 1.);
  }
  nDotL = max (0., dot (vn, sunDir));
  if (idObj == 2) nDotL *= nDotL;
  col = col * (0.2 + 0.8 * nDotL) + 0.1 * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
  col = mix (col, SkyBg (rd), pow (dstObj / dstFar, 4.));
  return col;
}

vec3 GrndCol (vec3 ro, vec3 rd, float dstGrnd)
{
  vec4 vn4;
  vec3 col, vn;
  float dFac, f, sh;
  dFac = 1. - smoothstep (0.15, 0.35, dstGrnd / dstFar);
  ro += dstGrnd * rd;
  vn = GrndNf (ro);
  col = mix (vec3 (0.65, 0.5, 0.1), vec3 (0.9, 0.75, 0.4), smoothstep (1., 4., ro.y));
  if (dFac > 0.) {
    if (vn.y > 0.3) {
      f = smoothstep (0.5, 2., ro.y) * smoothstep (0.3, 0.8, vn.y) * dFac;
      vn4 = RippleNorm (ro.xz, vn, 4. * f);
      vn = vn4.xyz;
      col *= mix (1., 0.95 + 0.05 * smoothstep (0.1, 0.3, vn4.w), f);
    }
    gId = PixToHex (ro.xz / hgSize);
    SetGConf ();
    if (isOcc) col *= 0.8 + 0.2 * smoothstep (0.5, 0.7, length (ro.xz -
       HexToPix (gId * hgSize) - rPos.xz));
  }
  sh = 1. - 0.6 * smoothstep (0.4, 0.7, Fbm2 (0.03 * ro.xz - tCur * vec2 (0.15, 0.)));
  col *= (0.2 + 0.1 * vn.y + 0.7 * sh * max (0., dot (vn, sunDir))) * (0.7 + 0.3 * dFac);
  col = mix (col, SkyBg (rd), pow (dstGrnd / dstFar, 4.));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col;
  float dstGrnd, dstObj, dstObjO, nRef;
  nRef = 0.;
  for (int k = VAR_ZERO; k < 3; k ++) {
    dstObj = ObjRay (ro, rd);
    dstGrnd = GrndRay (ro, rd);
    if (k < 2 && dstObj < min (dstGrnd, dstFar) && idObj == 3 && length (qHit.xy) < 0.99 * sRad) {
      ro += dstObj * rd;
      rd = reflect (rd, ObjNf (ro));
      ro += 0.01 * rd;
      ++ nRef;
      if (k == 0) dstObjO = dstObj;
    } else break;
  }
  if (min (dstObj, dstGrnd) < dstFar) col = (dstObj < dstGrnd) ? ObjCol (ro, rd, dstObj) :
     GrndCol (ro, rd, dstGrnd);
  else col = SkyCol (ro, rd);
  col *= pow (0.8, nRef);
  if (nRef == 1.) col = mix (col, SkyBg (rd), pow (dstObjO / dstFar, 4.));
  return clamp (col, 0., 1.);
}

mat3 EvalOri (vec3 v, vec3 a)
{
  vec3 w;
  vec2 cs;
  v = normalize (v);
  cs = sin (clamp (2. * (v.z * a.x - v.x * a.z), -0.2 * pi, 0.2 * pi) + vec2 (0.5 * pi, 0.));
  w = normalize (vec3 (v.z, 0., - v.x));
  return mat3 (w, cross (v, w), v) * mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.);
}

vec3 TrackPath (float t)
{
  return vec3 (20. * sin (0.07 * t) * sin (0.022 * t) * cos (0.018 * t) +
     13. * sin (0.0061 * t), 0., t);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 flMat, vuMat;
  vec4 mPtr, dateCur;
  vec3 ro, rd, col, fpF, fpB;
  vec2 canvas, uv, uvv;
  float el, az, asp, zmFac, sunEl, sunAz, t, hSum, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur = mod (tCur + 10., 36000.) + 30. * floor (dateCur.w / 7200.);
  asp = canvas.x / canvas.y;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  hgSize = 16.;
  gr.gFac = 0.1;
  gr.hFac = 1.3;
  gr.fWav = 1.9;
  gr.aWav = 0.45;
  dstFar = 300.;
  vuMat = StdVuMat (el, az);
  t = 3. * tCur;
  ro = TrackPath (t);
  fpF = TrackPath (t + 1.);
  fpB = TrackPath (t - 1.);
  flMat = EvalOri ((fpF - fpB) / 2., fpF - 2. * ro + fpB);
  hSum = 0.;
  for (float k = 0.; k < 7.; k ++) hSum += GrndHt (TrackPath (t + 0.5 * (k - 1.)).xz);
  ro.y = 4. + 8. * gr.hFac + hSum / 7.;
  sunAz = 0.002 * 2. * pi * tCur;
  sunEl = pi * (0.25 + 0.1 * sin (0.35 * sunAz));
  sunDir = vec3 (cos (sunEl) * sin (sunAz + vec2 (0.5 * pi, 0.)), sin (sunEl)).xzy;
  zmFac = 2.3;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi)) / zmFac;
    rd = flMat * (vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv / vec2 (asp, 1.))) *
       vec2 (asp, 1.), 1.)));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}
