// Bobby Blob Booping
// another day another blob
//
// looks like the iconic 'expansive reaction-diffusion' by Flexi
// https://www.shadertoy.com/view/4dcGW2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(0.);

    // grayscale
    vec4 data = texture(iChannel0, uv);

    // noise
    vec3 blue = texture(iChannel1, fragCoord/1024.).xyz;

    // normal
    vec3 unit = vec3(vec2(.005*blue.x), 0.);
    vec3 normal = normalize(vec3(T(uv-unit.xz)-T(uv+unit.xz),
                                 T(uv-unit.zy)-T(uv+unit.zy),
                                 T(uv)*.1));

    // lighting
    color += dot(normal, N(0,1,1))*.5+.5;

    // shadow
    color *= ss(.0,.2,data.r);

    fragColor = vec4(color, 1);
}

#define R iResolution.xy
#define ss(a,b,t) smoothstep(a,b,t)
#define N(a,b,c) normalize(vec3(a,b,c))
#define T(uv) texture(iChannel0, uv).r

// Dave Hoskins https://www.shadertoy.com/view/4djSRW
float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.*fragCoord-R)/R.y;
    float a;
    vec3 unit;

    // noise
    float rng = hash13(vec3(fragCoord, iFrame));

    // motion
    float time = iTime*2.5;
    float index = floor(time);
    float anim = fract(time);
    vec2 offset = mix(hash21(index), hash21(index+1.), anim)*2.-1.;
    if (iMouse.z > 0.) p -= (2.*iMouse.xy-R)/R.y;
    else p += offset*.6;

    // shape
    float shade = ss(.05,.0,(length(p)-.01));

    // neighboring
    vec4 frame = texture(iChannel0, uv);
    float life = frame.r;
    unit = vec3(vec2(.1*rng), 0.);
    life += T(uv-unit.xz)+T(uv+unit.xz)+T(uv-unit.zy)+T(uv+unit.zy)
           +T(uv+unit.xy)+T(uv+vec2(unit.x,-unit.y))+T(uv+vec2(-unit.x,unit.y))+T(uv-unit.xy);

    // gravity
    uv.y += .002 * life;

    // normal
    unit = vec3(vec2(.01*rng), 0.);
    vec3 normal = normalize(vec3(T(uv-unit.xz)-T(uv+unit.xz),
                                 T(uv-unit.zy)-T(uv+unit.zy),
                                 .5));

    // animated growth
    uv -= normal.xy * .04 * sin(life*3.14*2.+iTime*2.);

    // famebuffer fading
    frame = texture(iChannel0, uv);
    shade = frame.r - .0001 + shade;
    shade = clamp(shade, 0., 1.);

    fragColor = vec4(shade, 0, 0, 1);
}
