#define time iDate.w

#define TENSION 0.8

#define PI 3.1415926535

vec3 hue(float x)
{
    return sin(2.*PI*(x+vec3(0, 0.33, 0.66)))*0.5+0.5;
}

float hash21(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdCubicBezier(vec2 uv, vec2 s, vec2 c1, vec2 c2, vec2 e, float res)
{
    float r = 1e20;
    float st = 1./res;
    vec2 prev = s;
    for(float i = 0.; i < 1.; i += st)
    {
        float j = 1.-i;
        vec2 point = j*j*j*s+3.*j*j*i*c1+3.*j*i*i*c2+i*i*i*e;
        r = min(r, sdSegment(uv, prev, point));
        prev = point;
    }
    r = min(r, sdSegment(uv, prev, e));
    return r;
}

float map(vec2 uv)
{
    uv.x += round(hash21(floor(uv.yy)))*0.5;
    vec2 fp = floor(uv);
    uv = abs(fract(uv)-0.5);
    vec2 scale = vec2(0.25, 0.5);
    return (hash21(fp) < 0.3? sdCubicBezier(uv, vec2(-1)*scale, vec2(-1, -1.+TENSION)*scale, vec2(1, 1.-TENSION)*scale, vec2(1)*scale, 20.) :
                              abs(uv.x-scale.x))-0.03;
    //return round(hash21(floor(uv.yy)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec4 m = vec4((iMouse.xy*2.-iResolution.xy)/iResolution.y, iMouse.zw);
    float px = 1.5/iResolution.y;

    vec3 col = hue(fract(time/86400./2.))*(0.2+smoothstep(px*3., -px*3., map(uv*2.25+iDate.w*vec2(0, 0.2))));

    // Output to screen
    fragColor = vec4(col,1.0);
}
