// Particle Buffer


void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    float aspect = r.x/r.y;

    // if (uv.x * scale > 1. || uv.y * scale > 1.) return;
    // Prevent simulation of particles out of bounds
    // If we allow it then can resize at runtime


    if (iFrame < 30){

        O.xy = uv*scale;
        O.zw = normalize(hash(uvec3(U, iDate.w)).xy*2.-1.);
        return;

    }

    O = texture(iChannel0, uv);

    vec2 pos = O.xy;        
    vec2 vel = O.zw;


    vel += field(pos*6.2 + 2.1)*1e-2;


    float l = length(vel);
    if (l > 1.) vel = normalize(vel);
    vel *= 0.997; // try .998 with pos*2.2 above



    pos += vel*1e-3 * vec2(1, aspect);


    pos = mod(pos, 1.);

    O = vec4(pos, vel);

}

// Voronoi buffer


void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    if (iFrame < 10){
        O = vec4(0);
        return;
    }


    O = texture(iChannel1, uv);

    vec2 myParticles[4];
    vec4 particleDists;

    for (int i = 4; i-->0;){
        myParticles[i] = texture(iChannel0, decrypt(O[i], r)/scale).xy;
        particleDists[i] = length(myParticles[i] - uv);
    }


    for (int i = 10; i-->0;){

        for (int j = 4; j-->0;){

            vec3 a = hash(uvec3(U, iFrame - i + j*8))*2.-1.;
            vec3 b = hash(uvec3(U, iFrame * i - j  ))*2.-1.;
            vec3 c = hash(uvec3(U, iFrame + i - j*8))*2.-1.;

            vec2 samp = decrypt(
                texture(iChannel1,
                    mod(myParticles[j]
                        + (a.xy + vec2(a.z, b.x) + b.yz + c.xy) / (r.x/scale) 
                        // Gaussian-ish sampling
                        // we can use two vec3s to get three vec2s 
                        // and then another one for good measure
                    ,1.) // Mod loops around overflow coordinates
                )[i%5]
            ,r);

            vec2 newParticle = texture(iChannel0, samp/scale).xy;

            push(O, samp, r, particleDists[j], length(newParticle-uv));
            // Stores four particle IDs in the floats
        }
    }


    for (int i = 0; i < 2; i++){
        vec2 samp = hash(uvec3(U, iFrame * i)).xy;

        vec2 newParticle = texture(iChannel0, samp/scale).xy;

        push(O, samp, r, particleDists[0], length(newParticle-uv));
        // Completely random samples may not seem too critical, 
        // but they significantly improve the stability of the simulation
    }

}

