const float pi = 3.14159;
const float scale = 3.;
// Try big number to see what's going on. 3 is about the minimum that works semi coherently.
// Any improvements to accurately render an even larger quanitity of particles would be appreciated :D
// I have a few ideas but I'd love to learn about the community's knowledge


// #define debug
// Render particles directly, no visual processing

// #define display_voronoi
// Show the voronoi buffer


const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}
//hash by IQ https://www.shadertoy.com/view/XlXcW4
vec2 circleSample(uvec3 x){
    vec2 v,z;
    for(int i = 0;v=hash(x+uint(v*1e9)).xy, z=v*2.-1., i++<9 && length(z) > 1.;);
    return z;
}

mat2 rot( float rad ){
    return mat2(
        cos(rad), sin(rad),
        -sin(rad), cos(rad)
    );
}

vec2 decrypt(float d, vec2 r){
    return vec2(mod(d, r.x), floor(d/r.x))/r;
}

float encrypt(vec2 uv, vec2 r){
    return uv.x*r.x + floor(uv.y*r.y)*r.x;
}

void push(inout vec4 O, vec2 uv, vec2 res, float me, float new){
    ivec2 iuv = ivec2(floor(uv*res));
    ivec2 ires = ivec2(floor(res));

    float e = encrypt(uv, res);
    if (new < me && e != O.x && e != O.y && e != O.z && e != O.w){
        O.w = O.z; O.z = O.y; O.y = O.x;
        O.x = e;
    }

}

vec2 grad( ivec2 z )  // replace this anything that returns a random vector
{
    // 2D to 1D  (feel free to replace by some other)
    int n = z.x+z.y*11111;

    // Hugo Elias hash (feel free to replace by another one)
    n = (n<<13)^n;
    n = (n*(n*n*15731+789221)+1376312589)>>16;

#if 0

    // simple random vectors
    return vec2(cos(float(n)),sin(float(n)));

#else

    // Perlin style vectors
    n &= 7;
    vec2 gr = vec2(n&1,n>>1)*2.0-1.0;
    return ( n>=6 ) ? vec2(0.0,gr.x) : 
           ( n>=4 ) ? vec2(gr.x,0.0) :
                              gr;
#endif                              
}

float noise( in vec2 p )
{
    ivec2 i = ivec2(floor( p ));
     vec2 f =       fract( p );

	vec2 u = f*f*(3.0-2.0*f); // feel free to replace by a quintic smoothstep instead

    return mix( mix( dot( grad( i+ivec2(0,0) ), f-vec2(0.0,0.0) ), 
                     dot( grad( i+ivec2(1,0) ), f-vec2(1.0,0.0) ), u.x),
                mix( dot( grad( i+ivec2(0,1) ), f-vec2(0.0,1.0) ), 
                     dot( grad( i+ivec2(1,1) ), f-vec2(1.0,1.0) ), u.x), u.y);
}

vec2 field(vec2 uv){
    return vec2(noise(uv), noise(uv + 500.));
}

// Render Buffer


void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    float aspect = r.x/r.y;

    O = vec4(0);

    float minD = 2.;
    float x = 0.;
    for (int i = 0; i<2; i++){

        for (int j = 4; j-->0;){
            vec2 samp = decrypt(
                texture(iChannel1, 
                    uv + (circleSample(uvec3(U, iFrame * i << j)) / (r.x / scale))
                )[j]
            ,r)/scale;

            x = length(
                (texture(iChannel0, samp).xy - uv) * vec2(1, 1./aspect)
            );

            if (x < minD){
                minD = x;
            }
        }
    }

    #ifdef debug
    #else
        O = texture(iChannel2, uv
            //+ (vec2(scale*3e-3)/r)*rot(float(iFrame) * pi/2.)
        );
        O *= .96;
    #endif
    O += smoothstep(scale/r.x * .25, 0., minD);


    if (iMouse.z > 0.){
        if (texelFetch(iChannel3, ivec2(16, 0), 0).x > .0){
            O.x += max(0., 1.-length((iMouse.xy - U)/r/scale * vec2(1, 1./aspect)) * 500.);
        } else {
            O *= min(1., length((iMouse.xy - U)/r/scale * vec2(1, 1./aspect)) * 200.);   
        }
    }

}

// Post Processing


void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;

    #ifdef debug
        O = texture(iChannel2, uv);
    #else
        O = texture(iChannel3, uv);
        O *= .95;
        O += texture(iChannel2, uv)*1e-2;
    #endif

    // Because of SIMD, vector as opposed to operating on only O.x
    // should be the same speed right? I hope? Pls lmk in comments :)
}
