//https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract((2.0*abs(p/3.14 - 2.0*floor(p/(2.0*3.14)) - 1.0) - 1.0)*43758.5453123);
}

//https://www.shadertoy.com/view/Msf3WH
float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) )/2.0+0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y*30.0;
    float n0 = noise(0.05*uv - 1279.0 + iTime*0.1 + noise(0.1*uv - noise(vec2(iTime*0.01)) + 2354.0));
    float n1 = noise(0.05*uv - 1279.0 + iTime*0.1 + noise(0.1*uv - noise(vec2(iTime*0.01-0.2)) + 2354.0));
    float n2 = noise(0.05*uv - 1279.0 + iTime*0.1 + noise(0.1*uv - noise(vec2(iTime*0.01+0.2)) + 2354.0));
    vec3 lightDirection = normalize(vec3(iMouse.xy/iResolution.y - 0.5,0.5));
    vec3 normal = normalize(vec3(n0-n1,0.1,n0-n2));
    vec3 n = vec3(
        n1,
        0.8*n2,
        0.5*n0
    )*abs(n0 - 0.5)*8.0;

    vec3 col = n*max(dot(normal,lightDirection),0.0) + n*0.2;

    fragColor = vec4(col,1.0);
}
