void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv=cos(0.9*fragCoord/iResolution.xx)-(0.1*cos(iResolution.xx-iTime));


    // Time varying pixel color with more complex manipulation
    vec3 col = (iTime-20.*iTime * uv.xyx - vec3(0,2,4));//highter iTime-X stronger effect

    // Enhanced fractal noise based on time and space
    float noise = 0.0;
    float scale = 1.0;
    for (int i = 0; i < 10; i++) {
        noise += (0.5 - 0.5 * cos(dot(uv * scale, vec2(12.9898, 78.233)) + iTime)) / scale;
        scale *= 1.8;
    }

    // More complex transformation for evolving visuals
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    float angle = cos(iTime * 0.5) * 0.3;
    uv = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * uv;

    // Intricate pattern of moving shapes with fractal noise
    float dist = distance(uv, vec2(sin(iTime * 0.1), cos(iTime * 0.1)));
    float pattern = sin(dist * 12.0 - iTime * 3.0) + cos(dist * 12.0 - iTime * 3.0);
    pattern *= noise;

    // Advanced color manipulation
    col = (cos(0.5*angle)-(0.6*(2.-sin(col))))/(iTime * pattern);
    // Output to screen with dynamic alpha based on pattern and noise
    fragColor *=vec4(col,0.1/pattern - noise);
}
