#define S smoothstep
#define I iResolution
#define PI 3.1415
#define TAU PI * 2.
#define atl( a ) dot( a, a )
#define rot( a ) mat2( cos(a), sin(a), -sin(a), cos(a) )
// IQ
float rep(inout vec2 p, float rep) 
{

    float an = TAU/rep;

    float fa = (atan(p.y,p.x)+an*0.5)/an;
    float ia = floor( fa );

    float sym = an*ia;

    p *= rot(sym);

    return ia;
}

float Sy1( vec2 u ){
    float t = iTime * .05;
    t += .05 * sin( atan( u.x, u.y ) * 7. );
    return abs( fract( ( max( abs(u.x)-t, abs(u.y)-t ) ) * 7. ) - .5 );
}

float Sy2( vec2 u ){

    return abs( max(
        length(u*vec2(.5,1.)+vec2(-.43, 0.) ) - .01,
        length(u*vec2(.5,1.)+vec2(.43, 0.) ) - .01 ) - .5 );
}

float Sy3( vec2 u ){
    vec2 u2 = u;

    rep( u, 6. );
    u.x -= .1;

    rep( u, 3. );
    u.x -= .07;

    return min(
        Sy2(u2),
        abs(
            ( length( u ) - .01 ) - .05
        )
    );
}

float Sy4( vec2 u ){
    vec2 u2 = u;

    rep( u, 6. );
    u.x -= .1;

    rep( u, 2. );
    u.x -= .05;

    return min(
        Sy3(u2),
        abs(
            max(
               abs(u.x)-.01,
               abs(u.y)-.01
            )
         - .02)
    );
}

float Sy5( vec2 u ){
    u *= rot( iTime * -.05 );
    rep( u, 7. );
    u.x -= .8;

    rep(u,4.);
    u.x -= .3;

    u *= rot( PI * .5 );
    return Sy4( u );
}

float SyA( vec2 u ){
    return abs( 
        max(
            abs(u.x) - .05 * ( abs(u.y+.5) -.1 ),
            abs( u.y ) - .15
        ) - .01
    );
}

float SyAB( vec2 u ){
    u *= rot( iTime * -.25 );
    u *= rot( radians( 25. ) );
    rep( u, 7. );
    u.x -= .01;
    u *= rot( PI * -.5 );
    return SyA( u );
}

float SyAC( vec2 u ){
    u *= rot( radians( 25. ) );
    float i = rep( u, 7. );
    u.x -= 1.3;
    u *= rot( iTime * ((mod( i, 2. ) == 0.) ? -.25 : .25) );
    rep( u, 25. );
    u.x -= .4;
    u *= rot( PI * -.5 );
    return SyA( u );
}

void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    float A = min( I.x, I.y ), p;
    vec2 u = ( V * 2. - I.xy ) / A;

    // Time varying pixel color
    vec3 c = vec3( 0.47, 0.51, 0.52 );

    p = Sy1( u );

    c = mix(
        c,
        vec3( 0.47, 0.51, 0.52 ) * 1.2,
        // Neyret2
        atl( smoothstep( 30./A, 0.0, p ) ) * step( .07, length(u) )
    );

    p = Sy5( u );

    c = mix(
        c,
        vec3( 0.47, 0.51, 0.52 ) * 1.5,
        atl( smoothstep( 4./A, 0.0, p ) )
    );

    p = SyAB( u );

    c = mix(
        c,
        vec3( 0.47, 0.51, 0.52 ) * 1.9,
        atl( smoothstep( 4./A, 0.0, p ) )
    );

    p = SyAC( u );

    c = mix(
        c,
        vec3( 0.47, 0.51, 0.52 ) * 1.9,
        atl( smoothstep( 4./A, 0.0, p ) )
    );

    p = abs(
        ( length( u ) - .01 ) - .04
    );

    c = mix(
        c,
        vec3( 0.47, 0.51, 0.52 ) * 1.9,
        atl( smoothstep( 4./A, 0.0, p ) )
    );


    // Output to screen
    U = vec4(c,1.0);
}
