#define MIN_RAD 300.0
#define MAX_RAD 360.0
#define ANG2RAD 3.14  / 180.0
#define EYE_R 0.06
#define PAC_MAN_R 0.5

//cover from iq
float sdCircle(vec2 p, float r){
    return length(p) - r;
}  

float tri(float freq,float x){
    return mod(x,1.0/freq)*freq;
}

//
float sdPie(in vec2 p, in vec2 c, in float r ,float theta )
{ 
    float thetaRad = theta * ANG2RAD;
    mat2 rm = mat2(cos(thetaRad),sin(thetaRad),-sin(thetaRad),cos(thetaRad));
    p = rm * p;
    p.x = abs(p.x);
    float l = length(p) - r;
	float m = length(p - c*clamp(dot(p,c),0.0,r) );
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
   p.x = p.x + 1.0;
   float t1 = 3.14*(0.5+0.5*cos(iTime*0.52));
   float t3 = MIN_RAD + 60.0*smoothstep(0.0,0.7,sin(10.0*iTime));
   float t2 = iTime*100.0;
   float t = 0.5 * t3 * ANG2RAD;
   float d = sdPie(p,vec2(sin(t),cos(t)), PAC_MAN_R,-90.0);
   vec3 pacManColor =  vec3(0.949,0.827,0.027);
   vec3 bgColor = vec3(0.000,0.020,0.039);
   vec3 col = mix(pacManColor,bgColor,smoothstep(0.0,0.01,d));


   float eye_rad = -14.0*ANG2RAD;
   vec2 eyeP = p + 0.6 * PAC_MAN_R * vec2(sin(eye_rad),-cos(eye_rad));
   for(float i = 0.0; i <= 1.1;i += 0.1){
       vec2 beanP = p + vec2( 3.0*tri(0.9,iTime + i) - 3.0 ,0.0);
       col = mix(pacManColor,col,smoothstep(0.0,0.01,sdCircle(beanP,EYE_R)));
   }
   col = mix(vec3(0.0),col,smoothstep(0.0,0.01,sdCircle(eyeP,EYE_R)));
   col = mix( col, col, 1.0-smoothstep(0.0,0.5,abs(d)));

   fragColor = vec4(col, 1.0);
}
