vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract(.5+(a1)*(a2.x+a2.y))-.5)
        //abs(abs(fract(.5+(a1)*(a2.x+a2.y))-.5)+sign(a.x-a.y))
