void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(0.0);
    float t1 = 1.;
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy;

    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    uv -= draggedPos/iResolution.y/t1/2.0;
    vec2 t2 = vec2(0.);
    vec3 col = fractal(uv,t2);
    for(int i = 0; i < 1; i++){
        col = abs(fractal(uv,t2));
    }

    fragColor = vec4(col*2.,1.0);
}

int collatz(int initial, int steps){
    //makes some interesting rhythms
    for(int i = 0; i < steps; i++){
        if(initial%2==0) initial /= 2;
        else initial = initial*3+1;
    }
    return initial;
}
float collatz(float initial, float steps){
    return float(collatz(int(initial),int(steps)));
}

