#ifdef GL_ES
precision mediump float;
#endif

vec3 daygloColor(float value) {
    float hue = mod(iTime * 0.01 + value * 3.0, 1.0); // Slowed down the pulsing
    return mix(vec3(0.000,1.000,0.251), 
               vec3(0.6, 0.0, 1.0),
               hue);
}

vec2 rotate(vec2 uv, float angle) {
    return mat2(cos(angle), -sin(angle), 
                sin(angle), cos(angle)) * uv;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    // Slow rotation
    uv = rotate(uv, iTime * 0.05); // Adjust the 0.1 value for faster/slower rotation

    // Basic radial pattern
    float pattern = sin(18.0 * atan(uv.y, uv.x));

    // Some Picasso-esque abstract patterns
    pattern += step(0.5, mod(uv.x + uv.y * 2.5, 1.0)); // Slowed down the pulsing
    pattern += step(0.5, mod(uv.y * 5.0, 1.0));

    // Play with these values to adjust the patterns
    float mask = smoothstep(0.3, 0.41, length(uv));
    pattern *= mask;

    vec3 color = daygloColor(pattern);

    fragColor = vec4(color, 1.0);
}
