// This is a fork of testaccountplsignore's "inverted sphere structure" shader:
// https://www.shadertoy.com/view/DttGR8

// 149 chars - Fabrice RUTHLESSLY killed 3 chars!
void mainImage(out vec4 o, vec2 u)
{
    vec4 f = o*0.;
    for (f.y = .1*iTime; f.a++ < 1e2;)
         f += vec4(u/iResolution.y-.4, 1, 0)/4. * (.7 - length(o=fract(f+f+.5)-.5));
    o /= f.z; 
}


