#define saturate(x) clamp(x,0.0,1.0)
#define rot(x) mat2(cos(x), sin(x), -sin(x), cos(x))

highp float hash(
	highp vec2 x){
return fract(sin(
	dot(x,vec2(11,57)))*4567.0+iTime);
	}

highp float ripple(
	highp vec2 x,
	highp float k){
highp float r = hash(floor(x)),
d = length(fract(x)-0.5);

return smoothstep(0.05,0.025,distance(d, r*k))*
	smoothstep(0.5,0.475,d)*((1.0-r)*k);
	}

highp float ripple(
	highp vec2 x){
highp float result = 0.0;
for(int i = 0; i<3; i++){
x -= 6.0, x *= rot(0.3333),
result += ripple(x+iTime*0.5,float(i)/3.0+1.0);
	}
return result;
	}

highp vec3 norm(
	highp vec2 x) {
highp float

m = 0.01,
r = ripple(x-vec2(m,0)),
g = ripple(x-vec2(0,m)),
b = ripple(x);

r = r-b,
g = g-b;
b = 1.0-(r+g);

return (vec3(r+0.5,g+0.5,b*m+1.0));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = (fragCoord - iResolution.xy*0.5) / (
		iResolution.x, iResolution.y);

	vec3 n = norm(uv*6.0);

    if(uv.x>sin(iTime)){
    uv = reflect(vec3(uv,1),
    normalize(n*2.0-1.0)*0.125).xy;
    n = texture(iChannel0, uv).rgb;
    }

	fragColor = vec4(n,1);
}
