// ray casting bounding shape
const float CAM_DIST = 7.;
const float CAM_FLE = 1.3;
const float BB_DIST = 4.;
const float RAY_MIN_DIST = 0.5;
const float RAY_MAX_DIST = CAM_DIST+1.1*BB_DIST;

const int   MAX_MARCHING_STEPS = 300;
float RAY_STEP_FACTOR3 = 0.5;
const float RAY_PRECISION = 0.0001;

// shape transformation parameters
// icosahedron
const int PS_f = 20;
const int PS_e_f = 3;
const float PS_a0 = 12.;

// fog
const bool  FOG_ON = true;
const float FOG_DENSITY = 2.5E-3;

// data from sdf
vec4 mat = vec4(0);

float map(in vec3 p) {

    //// sdf's
    float rxy = 0., au = 0.0, av = 0.0;
    float cell_id = 0., sr = 0.; //sens of rotation, sig(sr): rotation direction, abs(sr) : number edges
    vec2 z = vec2(0.);

    //// map platonic solid cells to one cell
    vec3 pn = normalize(p);

    // icosahedron
    // projection from n cells to 1 cell. 
    pn = icosa_cells(pn, cell_id, sr);

    // invers stereographic projection
    z = pn.xy/(1.-pn.z);
    z = cIcosaPolyArc2disk(z);

    // set parameters
    vec2 e = vec2(1.8, 0.18);

    /// r-polygon-distribution
    float en = 1.+( (PS_a0<0.?e.x:e.y)-1. )*smoothstep(1.,5., abs(PS_a0));        
    float r = length(z); vec2 zn = z/r;
    z = dist(r, en) * zn;     

    /// stereograpic projection    
    pn = sp(vec3(z,sr));

    /// scale radius
    p = length(p) * pn;
    ////fractal level 0: Torus
    // 3D-space: cartesian3D => cylinder3D transformation
    au = atan(p.y, p.x);
    rxy = length(p.xy);
    // 2D-space: complex plane := radial plane in cylinderc3d coordinates  
    z = vec2(rxy, p.z);

    // 2Djulia :realaxis-translation, fraction == 2 => (torus,sphere,2spheres) 
    z = cmul(z,z); z.x -= PS_a0;

    // distance estimation shape
    float d = log(length(z));

    // torus complex plane, torus angle_u, platonic solid cell_id
    mat.xy = z; mat.z = au, mat.w = cell_id;
    return d;
}

float intersect(in vec3 ro, in vec3 rd, in float px) {
    float res = -1.0; // init no intersection

    // bounding shape BB
    vec2 dis = isphere( ro, rd , abs(BB_DIST));

    if( dis.y<0.0 ) return -1.0; // check BB behind came

    dis.x = max( dis.x, RAY_MIN_DIST ); // check min ray length
    dis.y = min( dis.y, RAY_MAX_DIST ); // check max ray length

    // raymarch signed distance field    
	float fh = RAY_STEP_FACTOR3;
    float t = dis.x; // ray starts at boundingbox
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) // max steps
    { 
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t; //th = 0.0001; // minimal bound surface hit
        float h = map( pos);
        if( t>dis.y || h<th ) break; // ray outside BB or hit.
        t += h*fh; // distance
    }
    if( t<dis.y ) // ray inside BB
    {
        res = t;
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy) - map(p-e.xyy),
		map(p+e.yxy) - map(p-e.yxy),
		map(p+e.yyx) - map(p-e.yyx) )
    	);
}

vec3 transform(in vec3 p, in float time) {
	// camera transformation
	if (iMouse.x > 0.0)
	{
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
    p.yz = crot(p.yz,-PI*0.002*time-0.3);
    p.zx = crot(p.zx,-PI*0.001*time-0.3);
	return p.xyz;
}

// textures
float grid_uv(in vec2 uv) {
    // uv grid alpha mask
    const float sig = 1.; // switch pattern sig in {-1.,+1.}
    vec2 sw = abs(fract(uv-0.25)-0.5)-0.25;
    float sdf = sig*min(sw.x,sw.y)*-max(sw.x,sw.y);
    float pp = fwidth(sdf);
    return smoothstep(+pp,-pp,sdf);
}

vec4 cells(in vec2 uv, in float ps_id ,in int ps_f) {
    // sectors, uv-grid
    vec4 C;
    float sdf = abs(abs(uv.y)-.05)-.05; float px = fwidth(sdf);

    C.a = 1.-smoothstep(px+.01,-px,sdf);
    C.rgb = h2rgb(ps_id/float(ps_f));      // sector -> col
    C.rgb *= (0.5+ 0.5 * grid_uv(uv))*0.7; // uv-grid -> value
    return C;
}

vec4 ribbon(in float uv_x, in float time) {
    float sdf = -(2.*pulsecollision(1.,1./3. * uv_x, time, 2./6.)-1.);
    float px = fwidth(sdf);
    return vec4( h2rgb(sdf -2./6.)
               , smoothstep(px+0.1,-px-0.1,sdf+0.2) );
}

vec4 nyancat(vec2 z, float scale, vec2 ju, float time) {   
    z.x = fract(z.x-time/scale);        // run, input z.x in [0., 1.], periodic & cyclic in x-direction (0)
    z.y = fract(z.y);                   // periodic in y-direction (comment in out)
    z *= scale;
    z.y -= ju.x*abs(cos(PI*ju.y*time)); // jump, amplitude, frequency
    vec4 C = vec4(0.);
    if ((.05<z.x&&z.x<.9)&&(0.<z.y&&z.y<.65)) { // BB rectangle
        C = texture(iChannel1, vec2(40./256.*(z.x+floor(6.*fract(ju.y*time))),z.y+.2));
        C.rgb = srgb2rgb(C.rgb);
    }
    return C;
}

vec4 truchet(in vec2 uv, float time) {
    // fork: https://www.shadertoy.com/view/msVfzR by byt3_m3chanic   
    // coords
    vec2 dv = fract(uv)-.5,
         id = floor(uv);

    // random
    float rnd = hash21(id);

    // colors
    vec4 O; vec4 C;
    float sdf; float px;

    // signed distance functions
    if(rnd<.5) dv.x = -dv.x;

    vec2  gx = length(dv-.5)<length(dv+.5)
             ? vec2(dv-.5)
             : vec2(dv+.5);  

    float cx = (fract(rnd*77.57+time)>.75)
             ? min(abs(dv.x),abs(dv.y))
             : length(gx)-.5;

    // tiles
    sdf = abs(cx);  px = fwidth(sdf);
    C.a = smoothstep(px,-px,sdf-.12);
    C.rgb = h2rgb(
                time
                -uv.x*.075
                +fract(rnd*147.32+(time*.05))*.35
                )*.5;
    O = C;

    // boder lines
    sdf = abs(abs(cx)-.12); px = fwidth(sdf);
    C.a = smoothstep(px+.02,-px,sdf-.02);
    O = mix(O,vec4(1.), C.a);
    return O;
}

//
void mainImage(out vec4 O, in vec2 fragCoord) {
    // slider mapping
    RAY_STEP_FACTOR3 = (0.5-0.0001)* pow(RAY_STEP_FACTOR3, 3.) + 0.0001;

    // anti-aliasing to take a picture
    vec3 tot = vec3(0.0);
    #define AA 1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {       
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 uv = (2.0*(fragCoord+o) - iResolution.xy)/iResolution.y;

        // camera viewport --> worldspace
        vec3 ro = vec3(0,0,-CAM_DIST);
        vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
             ro = transform(ro,iTime);
             rd = normalize(transform(rd,iTime));

        // ray
        float px = 2.0/( iResolution.y*CAM_FLE );
        float t = intersect( ro, rd, px ); // distance hit ray surface

        // color
        // drawing layers
        // sdf = ... ; px = fwidth(d);
        // C.a = smoothstep(px+.035,-px,sdf-.125);
        // C.rgb = ... ;
        //   near -> far : if(O.a<1.) { O += (1.-O.a) * vec4( C.rgb, 1 ) *C.a;}
        //   far -> near :  O = mix( O, vec4( C.rgb, 1), C.a );
        vec3 col_sun = vec3(1.,1.,0.8);
        vec3 col_sky = 0.5 * vec3(0.7,0.7,1.);

        // background
        vec4 C = vec4(col_sky,1.);
        O = C;

        // object surface
        if (t >= 0.) {
            // surface properties
            vec3 p = ro + rd * t;
            vec3 n = calcNormal( p, 0.001);

            // platonic solid sector id
            float ps_id = mat.w;

            // uv-coords
            vec2 mat_uv = 0.5*vec2(mat.z, atan(mat.y,mat.x))/PI;
            vec2 mat_ny = mat_uv;

            mat_uv.xy *= vec2(3.*6.,4.); // uv-grid

            mat_ny += vec2(0.,1./3.);    // nyancat
            mat_ny *= vec2(6.,1.);

            // draw texture layers from back to front
            O = vec4(vec3(0.1,0.1,1.)*0.7,1.);

            C = cells(mat_uv, ps_id, PS_f);
            O = mix(O, vec4(C.rgb, 1.), C.a);            

            C = truchet(mat_uv, 0.05*iTime);
            O = mix(O, vec4(C.rgb, 1.), C.a);

            C = nyancat(mat_ny.xy, 3., vec2(0.1,1.), 0.3*iTime); 
            O = mix(O, vec4(C.rgb, 1.), C.a);

            C = ribbon(mat_uv.x, 0.0125*iTime);
            O = mix(O, vec4(C.rgb, 1.), C.a);

            // lighting
            vec3  came_li_o         = transform(vec3(-10.,10.,-10.),iTime); // origine
            vec3  came_li_dir       = normalize(p - came_li_o);             // direction
            vec3  came_li_col       = vec3(1.,1.,.5);                       // color
            float came_li_spec_shi  = 10.;                                  // shininess          

            O.rgb += O.rgb * 0.1 * col_sky;
            O.rgb += O.rgb * 0.5 * came_li_col * clamp(dot(-came_li_dir, n), 0., 1.);

            float cosa = pow(clamp(dot(reflect(came_li_dir, n), -rd), 0., 1.), came_li_spec_shi);           
            vec3 spec_col = vec3(0.);          
            if (cosa > 0.) {
              spec_col = came_li_col * cosa;    
            }
            O.rgb += 0.4 * spec_col;

            // fresnel
            O.rgb += pow(clamp(1. - dot(n, -rd), 0., 1.), 5.) * 0.4 * col_sky;

            // fog
            if(FOG_ON)
                O.rgb = mix(O.rgb, col_sky, 1.0 - exp(-FOG_DENSITY * pow(t,3.)));    
        }
        tot += O.rgb;
    }
    tot /= float(AA*AA);

    // gamma correction
    O = vec4(rgb2srgb(tot),1.);
}

// References:
//   https://people.maths.ox.ac.uk/trefethen/lightning.html
//   https://iquilezles.org/

// const
const float PI = abs(atan(0.,-1.));

/// 1D transformations
float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    /*
    x  : periodic in [0,1]
    x0 : offset
    pw : pulse width in [0,1]
    pf : pulse frequency 
    */
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5); // x_normalized triangle wave function moving in +x_direction
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5); // x_normalized triangle wave function moving in -x_direction
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );   // pulse moves in +x_direction
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );   // pulse moves in -x_direction
    return (pp+pn); // in[0.,2.]
    //return (pp+pn)-(pp*pn);// in [0.,1.]
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453);
}

/// 2D transformations
/// complex
vec2 cmul(vec2 za,vec2 zb){
    return za*mat2(zb.x,-zb.y,zb.yx);}

vec2 crot(vec2 z,float a){
    return cmul(vec2(cos(a),sin(a)),z);}

vec2 cinv(vec2 z){
    return z*vec2(1,-1)/dot(z,z);}

vec2 cdiv(vec2 z, vec2 w){
    return cmul(z,cinv(w));}

// polynomial barycentric approximation
vec2 cIcosaPolyArc2disk(vec2 z){
    const int NUM_j = 18; // poles
    const vec2[] rf = vec2[NUM_j]
    (vec2(-1.0000,  0.0081)
    ,vec2( 0.9973, -0.0741)
    ,vec2( 0.5000,  0.8660)
    ,vec2( 0.5000,  0.8660)
    ,vec2( 0.5000, -0.8660)
    ,vec2( 0.5000, -0.8660)
    ,vec2(-0.5628, -0.8266)
    ,vec2( 0.7134,  0.7008)
    ,vec2(-0.6770,  0.7360)
    ,vec2( 0.5660, -0.8244)
    ,vec2( 0.4380, -0.8990)
    ,vec2( 0.4776,  0.8786)
    ,vec2(-1.0000,  0.0000)
    ,vec2(-1.0000, -0.0000)
    ,vec2(-0.9975, -0.0702)
    ,vec2( 0.5013,  0.8653)
    ,vec2( 0.4987, -0.8668)
    ,vec2(-1.0000, -0.0015));

    const vec2[] rw = vec2[NUM_j] 
    (vec2( 0.1913, -0.0   )
    ,vec2( 0.0675, -0.5245)
    ,vec2( 0.0000,  0.0000)
    ,vec2(-0.0217,  0.0173)
    ,vec2( 0.0000,  0.0000)
    ,vec2( 0.0227, -0.0136)
    ,vec2(-0.1778,  0.1022)
    ,vec2( 0.4571, -0.0498)
    ,vec2( 0.1298,  0.1710)
    ,vec2(-0.1199, -0.3334)
    ,vec2(-0.1620,  0.2846)
    ,vec2(-0.1091,  0.1608)
    ,vec2(-0.0000,  0.0000)
    ,vec2( 0.0220,  0.0049)
    ,vec2(-0.2238,  0.0539)
    ,vec2( 0.1257,  0.0310)
    ,vec2(-0.1355, -0.0377)
    ,vec2(-0.0667,  0.1333));

    const vec2[] rz = vec2[NUM_j]
    (vec2(-0.3057,  0.0242)
    ,vec2( 0.1872, -0.0101)
    ,vec2( 0.1691,  0.2929)
    ,vec2( 0.1692,  0.2924)
    ,vec2( 0.1691, -0.2929)
    ,vec2( 0.1686, -0.2927)
    ,vec2(-0.1024, -0.1571)
    ,vec2( 0.1839,  0.1273)
    ,vec2(-0.1198,  0.1468)
    ,vec2( 0.1795, -0.1922)
    ,vec2( 0.0805, -0.2533)
    ,vec2( 0.1099,  0.2669)
    ,vec2(-0.3383,  0.0   )
    ,vec2(-0.3378, -0.0004)
    ,vec2(-0.2597, -0.0569)
    ,vec2( 0.1715,  0.2728)
    ,vec2( 0.1505, -0.2850)
    ,vec2(-0.3221, -0.0121));

    // polynomial barycentric representation
    vec2 zn = vec2(0.); vec2 zd = vec2(0.);       
    for(int j = 0; j<NUM_j; j++)
    {
        vec2 zj = cdiv( rw[j], (z-rz[j]));
        zd += zj; // sum denominator
        zn += cmul(rf[j], zj); // sum nominator
    }
    return cdiv(zn, zd);
}

/// 3D transformations
mat3 rot3(in vec3 v, in float a) {// rotation from axis angle 
 float c = cos(a);
 float s = sin(a);	
 return mat3
 (c + (1.0 - c) * v.x * v.x,               (1.0 - c) * v.x * v.y - s * v.z,     (1.0 - c) * v.x * v.z + s * v.y,
      (1.0 - c) * v.x * v.y + s * v.z, c + (1.0 - c) * v.y * v.y,               (1.0 - c) * v.y * v.z - s * v.x,
      (1.0 - c) * v.x * v.z - s * v.y,     (1.0 - c) * v.y * v.z + s * v.x, c + (1.0 - c) * v.z * v.z);
}

// map spherical platonic solid pyramidal-face-cells to one cell located at -z-axis.
vec3 icosa_cells(in vec3 p, out float cell_id, out float sr){
	// icosahedron
	const float phi = (0.5+sqrt(1.25));
	// cell-wall-face-normals defined by 2 edge points and the origine.
	//----------------------------------------
    bool ZpYp_ZpYn = dot(vec3( 1., 0., 0.) ,p)>=0.; // yz-rectangle edge

	bool XpZp_ZpYp = dot( cross(vec3(+phi,0,+1),vec3(0,+1,+phi)) ,p)>=0.;
	bool XpZp_ZpYn = dot( cross(vec3(+phi,0,+1),vec3(0,-1,+phi)) ,p)>=0.;

	bool XnZp_ZpYp = dot( cross(vec3(-phi,0,+1),vec3(0,+1,+phi)) ,p)>=0.;
    bool XnZp_ZpYn = dot( cross(vec3(-phi,0,+1),vec3(0,-1,+phi)) ,p)>=0.;
    //----------------------------------------
    bool XpZp_XpZn = dot(vec3( 0., 1., 0.) ,p)>=0.; // zy-rectangle edge

	bool YpXp_XpZp = dot( cross(vec3(+1,+phi,0),vec3(+phi,0,+1)) ,p)>=0.;
	bool YpXp_XpZn = dot( cross(vec3(+1,+phi,0),vec3(+phi,0,-1)) ,p)>=0.;

	bool YnXp_XpZp = dot( cross(vec3(+1,-phi,0),vec3(+phi,0,+1)) ,p)>=0.;
    bool YnXp_XpZn = dot( cross(vec3(+1,-phi,0),vec3(+phi,0,-1)) ,p)>=0.;
	//----------------------------------------
	bool YpXp_YpXn = dot(vec3( 0., 0., 1.) ,p)>=0.; // xy-rectangle edge

	bool ZpYp_YpXp = dot( cross(vec3(0,+1,+phi),vec3(+1,+phi,0)) ,p)>=0.;
	bool ZpYp_YpXn = dot( cross(vec3(0,+1,+phi),vec3(-1,+phi,0)) ,p)>=0.;

	bool ZnYp_YpXp = dot( cross(vec3(0,+1,-phi),vec3(+1,+phi,0)) ,p)>=0.;
    bool ZnYp_YpXn = dot( cross(vec3(0,+1,-phi),vec3(-1,+phi,0)) ,p)>=0.;
	//----------------------------------------   

	// cells
    //const float a = atan(1.5-sqrt(1.25)); // angle face center -> edge center
    const float a = atan(0.5*(3.-sqrt(5.)));
    vec2 ei_yy = vec2(cos(a),sin(a));
    const float b = PI/3.; // 60Â°
    vec2 ei_zz = vec2(cos(b),sin(b));
    float a_x = 0.; float a_y = 0.; float a_z = 0.;
	// ---------------------------------------------------------------
    if     ( ZpYp_ZpYn && !XpZp_ZpYn   &&  XpZp_ZpYp) { cell_id = 0.;
        //p.xyz = p.xyz;
        #if 0
        a_y = -a+PI;
        #else
        p.zx = -p.zx;
        a_y = -a;
        #endif
	}
	else if(!ZpYp_ZpYn &&  XpZp_ZpYn   && !XpZp_ZpYp) { cell_id = 1.; /*opposite side*/	
        //p.xyz = p.xyz;
		a_y = -a;
	}
	//
	else if(!ZpYp_ZpYn &&  XnZp_ZpYn   && !XnZp_ZpYp) { cell_id = 2.;
        //p.xyz = p.xyz;
		a_y = +a-PI;
		a_z = +b;
	}
	else if( ZpYp_ZpYn && !XnZp_ZpYn   &&  XnZp_ZpYp) { cell_id = 3.; /*opposite side*/
        //p.xyz = p.xyz;
		a_y = +a;
		a_z = -b;
	}
	// ---------------------------------------------------------------
    else if( YpXp_YpXn && !ZpYp_YpXn   &&  ZpYp_YpXp) { cell_id = 4.;
        p.xyz = p.zxy;
        a_y = -a+PI;
    }
    else if(!YpXp_YpXn &&  ZpYp_YpXn   && !ZpYp_YpXp) { cell_id = 5.; /*opposite side*/
        p.xyz = p.zxy;
		a_y = -a;
    }
    //
    else if(!YpXp_YpXn && !ZnYp_YpXp   &&  ZnYp_YpXn) { cell_id = 6.;
        p.xyz = p.zxy;
		a_y = +a-PI;
		a_z = +b;        
    }
    else if( YpXp_YpXn &&  ZnYp_YpXp   && !ZnYp_YpXn) { cell_id = 7.; /*opposite side*/
        p.xyz = p.zxy;
		a_y = +a;
		a_z = -b;
    }
	// ---------------------------------------------------------------
    else if( XpZp_XpZn && !YpXp_XpZn   &&  YpXp_XpZp) { cell_id = 8.;
        p.xyz = p.yzx;
        a_y = -a+PI;
    }
    else if(!XpZp_XpZn &&  YpXp_XpZn   && !YpXp_XpZp) { cell_id = 9.; /*opposite side*/
        p.xyz = p.yzx;
		a_y = -a;
    }
    //
    else if(!XpZp_XpZn && !YnXp_XpZp   &&  YnXp_XpZn) { cell_id = 10.;
        p.xyz = p.yzx;
		a_y = +a-PI;
		a_z = +b;
    }
    else if( XpZp_XpZn &&  YnXp_XpZp   && !YnXp_XpZn) { cell_id = 11.; /*opposite side*/
        p.xyz = p.yzx;
		a_y = +a;
		a_z = -b;
    }
	// ---------------------------------------------------------------    
    else if(!XpZp_ZpYp && !YpXp_XpZp   && !ZpYp_YpXp) { cell_id = 12.;
        //p.xy = p.xy;
        mat3 m3 = rot3(normalize(vec3(phi,0.,1.)), 2.*PI/5.); p = p*m3;
        a_y = -a+PI;
    }
    else if( XpZp_ZpYp &&  YpXp_XpZp   &&  ZpYp_YpXp) { cell_id = 13.; /*opposite side*/
        //p.xy = p.xy;
        mat3 m3 = rot3(normalize(vec3(phi,0.,1.)), 2.*PI/5.); p = p*m3;
		a_y = -a;
    } 
    // ---------------------------------------------------------------
    else if( XnZp_ZpYp &&  ZpYp_YpXn   &&  (YnXp_XpZn)) { cell_id = 14.;
        p.xy = -p.xy;
