void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv-=vec2(-1.2,-0.5);
    uv*=5.0;
    uv.y+=iTime;
    vec3 col=vec3(0.5,0.02,0.2);
    uv.y=mod(uv.y,5.4);
    col=Edificio(uv,col);

    fragColor = vec4(col,1.0);
}
