vec2 f1(vec2 a){
    return
        fract(a)
    ;
}

vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(f1((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    uv -= draggedPos/iResolution.y/t1;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 8; k++){
        bool b = uv.x>uv.y;
        uv =
            abs(.5+uv+t2)/1.5
            //abs(.5+uv+t2/1.5)
            //abs(.5+uv+t2)/1.5
        ;

        //WOW!
        //uv = (fract(vec2(uv+vec2(.5,1.5))*1.5)-.5)/1.5;

        //uv = abs(fract(vec2(uv+vec2(.5,1.5))*1.5)-.5)/1.5;
        b = uv.y>uv.x || !b;
        if(b) uv = uv.yx;

        t2 =
            triangle_wave(uv-.5)
        ;
        uv =
            t2-triangle_wave(uv.yx)
        ;
        //adds more detail
        //if(uv.x>-.25)
        col =
            vec3(col.zx,max(uv.x-uv.y*float(b),0.))
        ;
    }
    fragColor = vec4((col*2.),1.0);
}

void distort(inout float a, inout float b){
    for(float i = 1.; i < mod(floor(a/b),3.); i++){
        a /= 2.;
        a += mod(floor(a/2.),b);
    }
}

