float prime_rhythm(float time){
    return (1.+fmod(time/5.,2.))/(1.+fmod(time/3.,2.));
    //return (1.+fmod(time/2.,2.))*(1.+fmod(time/3.,2.))/(1.+fmod(time/5.,2.))/(1.+fmod(time/7.,2.))*(1.+fmod(time/11.,2.));
}

