#define LINES 10.0
#define BLOBS 50.0
#define FF 10.0
#define SMOOTHNESS 1.5
#define STIFFNESS 0.5
float rand(float x) {
    return fract(sin(dot(vec2(x,x),
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}


// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

vec2 cubic_hermite(vec2 p0, vec2 p1, vec2 v0, vec2 v1, float t) {
    float sq = t*t;
    float cu = t*t*t;

    float h1 = 2.*cu-3.*sq+1.;
    float h2 = -2.*cu+3.*sq;
    float h3 = cu - 2.*sq + t;
    float h4 = cu - sq;
    return h1*p0+h2*p1+h3*v0+h4*v1;
}

//sinc comb. Good start: sharpness of 10
float comb(float phase, float density, float sharpness){
    float signed_mod = mod(phase,density) - density/2.0;
    float sinc = sin(signed_mod * sharpness)/(signed_mod*sharpness);
    return pow(sinc,SMOOTHNESS);
}

vec2 random_path(float t, float seed){
    vec2 pos0 = random2(vec2(seed,floor(t)));
    vec2 pos1 = random2(vec2(seed,floor(t+1.)));
    vec2 vel0 = random2(vec2(floor(t),seed));
    vec2 vel1 = random2(vec2(floor(t+1.),seed));
    return cubic_hermite(pos0,pos1,vel0,vel1,fract(t));
}

vec2 combed_path(float t, float seed, float comb) {
    float offset = 2.0*comb - 1.8;
    vec2 pos0 = random2(vec2(seed,floor(t)));
    vec2 pos1 = random2(vec2(seed,floor(t+1.)));
    pos0.y = pos0.y + offset;
    pos1.y = pos1.y + offset;
    //pos0.x = pos0.x*(1.0-comb);
    //pos1.x = pos1.x*(1.0-comb);
    vec2 vel0 = random2(vec2(floor(t),seed));
    vec2 vel1 = random2(vec2(floor(t+1.),seed));
    return cubic_hermite(pos0,pos1,vel0,vel1,fract(t));
}

float blob(vec2 uv, vec2 pos, float brightness, float spotlight, float cutoff){
    float d = distance(uv,pos);
    float mul = 2.0*(d*d*d)/(cutoff*cutoff*cutoff) - 3.0*(d*d)/(cutoff*cutoff) + 1.0;
    return d < cutoff ? mul*brightness*exp(-spotlight*d) : 0.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv *= 2.;
    float a = 2.2;//+noise(vec2(iTime*0.01,1.0))*10.;
    float b = 4.0;//+noise(vec2(iTime*0.01,2.0))*10.;
    float c = 100.0;
    float field = 0.0;
    vec3 color_field = vec3(0.0,0.0,0.0);
    for(float i = 0.0; i<BLOBS; i = i + 1.0){
        //field += blob(uv, random_path(iTime, i), a, b,c);
        float t = iTime * 0.03 * FF + i*1.0/BLOBS;
        vec2 pos = combed_path(t, i,comb(iTime*0.05*FF+i,BLOBS,2.0));
        float blob = blob( uv, pos, a, b,c);
        vec3 color = vec3(rand(i),rand(i+0.1),rand(i-0.1));
        float noise = rand(blob);
        //color = mix(vec3(0.0,0.0,0.0),color,noise+0.8);
        color_field += color
                    * blob;
    }
    //vec3 col = vec3(field);
    vec3 col = color_field;
    fragColor = vec4(col,1.0);
}
