v3 map( v2 p ){
    v3 c = v3(0.0);
    F  d  = S(e.x,e.y,lvl(-p));
    F  d2 = S(e.x,e.y,L(p+Bot.xy )-Rad);
    F  d3 = S(0.,0.001,L(p+Player.xy )-Rad);
       if(W(d2,d3)>0.0){ c = v3(d); };
       if(d2<d3){ c = v3(1.,0.,0.); }
       if(d3<d2){ c = v3(0.,1.,0.); }
    R  c;
}

V loop(out v4 O, in v2 U) {
    F  z = 3.0;
    v2 uv = (z*U-iR.xy)/iR.y;
    v3 c = map(uv);
       O = v4(c,1.0);
  }

#define Rad 0.1
#define Bot texelFetch(iChannel0, ivec2(0, 0), 0)
#define Player texelFetch(iChannel0, ivec2(1, 0), 0)

#define loop mainImage

#define e  v4(0.0,0.001,0.5,20.0)

#define iR iResolution
#define iF iFrame
#define iT iTime
#define tf(t) texelFetch(iChannel0, ivec2(t), 0)

#define v2 vec2
#define v3 vec3
#define v4 vec4
#define m2 mat2
#define m3 mat3
#define m4 mat4
#define i2 ivec2
#define i3 ivec3
#define i4 ivec4

#define A  abs
#define D  dot
#define F  float
#define I  int
#define L  length
#define M  max
#define N  normalize
#define R  return
#define S  smoothstep
#define V  void
#define W  min
#define Y  reflect

m2 rot(  F  a ){ F s=sin(a),c=cos(a); R m2(c,-s,s,c); }
F  box(  v2 p, v2 size ){ v2 d=A(p)-size; R L(M( d, v2(0) )) + W(M(d.x,d.y), 0.0 ); }
F  ball( v2 p, F  r ){ R L(p)-r; }

F  lvl(v2 p){
    F d;
    p += v2(-.1,.25);
    d = ball(p,.5);
    d = W(box(p+v2(1.,.25),v2(1.0,.25)),d);
    d = -W(ball(p+v2(2.,.5),.5),d);
    d = W(d,box(p+v2(1.0,-0.01),v2(1.,.00))-.01);
    d = W(d,box(p+v2(1.0,0.51),v2(1.,.00))-.01);
    R d;
}

v2 nor(v2 p) { R N( v2( lvl(p+e.yx)-lvl(p-e.yx), lvl(p+e.xy)-lvl(p-e.xy) ) ); }

v4 physics( i2 uv, I element ){
    v4 body = tf(v2(element, 0));
       body.xy += body.zw;
    F  d = lvl(body.xy);
    if( d < Rad ){
       v2 h = nor(body.xy);
       body.xy += h * (Rad - d);
       body.zw  = Y(body.zw, h);
    }
    R body;
}

v4 engine( i2 uv ){
    v4 d;
    v3  eps = v3( 0.0, 0.01, 0.005 );
    if( uv == i2(0,0) ){
        if( iF == 0 ) d = eps.xxzx;
        if( iF >  0 ) d = physics(i2(uv),0);
    }
    if( uv == i2(1,0) ){
        if( iF == 0 ) d = eps.yyzx;
        if( iF >  0 ) d = physics(i2(uv),1);
    }
    R d;
}

V loop(out v4 O, in v2 U) {
    i2 uv = i2(U);
       O  = v4(0.);
       O  = engine(uv);
}
