#define rep(p,s) (fract(p/s-0.5)-0.5)*s

float box(vec3 p, vec3 s) {
  p=abs(p)-s;
  return max(p.x, max(p.y,p.z));
}

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);  
}


float tick (float t ) {
  float i = floor(t);
  float r = fract(t);
  r = sqrt(r);
  return r + i;

}
float grid(vec3 p) {


  vec3 p2 = p;
  p2.y = (fract(p.y)-0.5);
  float d = box(p2, vec3(10.0,0.2,0.05));

  p.x = (fract(p.x)-0.5);

  d = min(d, box(p, vec3(0.2,5,0.05)));

  return d;
}
float hash(vec3 p)  
{
    p  = 17.0*fract( p*0.3183099+vec3(.11,.17,.13) );
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}


float sph(vec3 i, vec3 f, vec3 c) {
    return length(f-c) - hash(i+c)*.3;
}
float spheres( in vec3 p )
{

    vec3 i = floor(p);
    vec3 f = fract(p);



    return min(min(min(sph(i,f,vec3(0,0,0)),
                       sph(i,f,vec3(0,0,1))),
                   min(sph(i,f,vec3(0,1,0)),
                       sph(i,f,vec3(0,1,1)))),
               min(min(sph(i,f,vec3(1,0,0)),
                       sph(i,f,vec3(1,0,1))),
                   min(sph(i,f,vec3(1,1,0)),
                       sph(i,f,vec3(1,1,1)))));

}

float map(vec3 p) {

  // geo


  float d =  spheres(p);



  return d;
}

float rnd(vec2 uv) {
  return fract(dot(sin(uv*754.655+uv.yx*942.742),vec2(3847.554)));
}

float rnd(float t) {
  return fract(sin(t*472.355)*655.644);
}

float curve(float t) {
  return mix(rnd(floor(t)), rnd(floor(t)+1.0), smoothstep(0.0,1.0,fract(t)));  
}




float shadow(vec3 p, vec3 l, float maxdist, int stepcount, float limit) {
  float shad=1.0;
  float dd=0.0;
  for(int i=0; i<stepcount; ++i) {
    float d=min(map(p), maxdist);
    if(d<limit) {
      shad=0.0;
      break;
    }
    if(dd>=maxdist) {
      break;
    }

    p += l*d;
    dd+= d;

  }
  return shad;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);







 // vec3 r=normalize(vec3(-uv, 1));


  // nav

  vec3 s =vec3(0,0,10);
  s.xz *= rot(iTime * .1);
  s.yz *= rot(iTime * .04);
  s.yx *= rot(iTime * .061);
  vec3 t = vec3(0,0,0);


  vec3 cz=normalize(t-s);
  vec3 cx=normalize(cross(cz,vec3(0,-1,0)));
  vec3 cy=normalize(cross(cz,cx));
  vec3 r=normalize(cx*uv.x+cy*uv.y+cz*0.7);





  // march

  vec3 p=s;
  float dd=0.0;
  const float maxdist = 100.0;

  for(int i=0; i<100; ++i) {

    float d=map(p);
    if(d<0.001) break;
    if(dd>maxdist) { dd=maxdist; break;}
    p+=r*d;
    dd+=d;

  }



  // volume

  float rand=rnd(uv);

  vec3 lightpos = vec3(3);
  vec3 lightfocus = vec3(3);
  float lightangle = .1;

  float t1 = tick(iTime);
  float t2 = tick(iTime * .71);
  float t3 = tick(iTime * .55);
  lightpos.yz *= rot(t1);
  lightpos.xz *= rot(t2);
  lightpos.yx *= rot(t3);

  lightfocus.yz *= rot(t1 * .7);
  lightfocus.xz *= rot(t2 * .8);
  lightfocus.yx *= rot(t3 * .91);




  const int volsteps=50;
  float voldist = 15.0;
  float stepsize = voldist/float(volsteps);
  vec3 lr=r*stepsize;
  vec3 lp=s + lr*rand;
  float stepdist=rand*stepsize;
  vec3 atcol=vec3(0);

  for(int i=0; i<volsteps; ++i) {

    if(stepdist>dd) {
      break;
    }

    vec3 lv = lightpos-lp;
    float ldistvol = length(lv);
    lv = normalize(lv);
    float shadvol = shadow(lp, lv, ldistvol, 10, 0.01);
    if ( dot( normalize(lv), normalize(lp) ) > -1.) {
      atcol += 5./(0.01+(pow(ldistvol,1.5))) * shadvol;
    }

    lp+=lr;
    stepdist+=stepsize;

  }



  vec2 off=vec2(0.01,0);
  vec3 n=normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));

  vec3 l=normalize(lightpos - p);

  float ldist = length(lightpos - p);


  float shad = shadow(p + n * 0.2, l, ldist, 30, 0.01);

  vec3 col=vec3(0);

  col += atcol * .02;



  fragColor = vec4(col, 1);
}
