// Closest point on quad. The blue sphere is the evaluation
// point.The orange sphere marks the point on the quad
// closest to the blue sphere. Related shaders:

// Closest points to other primitives in this list:
//
//    https://www.shadertoy.com/playlist/wXsSzB


#if HW_PERFORMANCE==0
#define AA 2
#else
#define AA 5
#endif

float dot2( in vec3 v ) { return dot(v,v); }

vec3 closestQuad( in vec3 v0, in vec3 v1, in vec3 v2, in vec3 v3, in vec3 p )
{
    vec3 v10 = v1 - v0; vec3 p0 = p - v0;
    vec3 v21 = v2 - v1; vec3 p1 = p - v1;
    vec3 v32 = v3 - v2; vec3 p2 = p - v2;
    vec3 v03 = v0 - v3; vec3 p3 = p - v3;

    vec3 nor = cross( v10, v03 );

    if( dot(cross(v10,nor),p0)<0.0 ) return v0 + v10*clamp( dot(p0,v10)/dot2(v10), 0.0, 1.0 );
    if( dot(cross(v21,nor),p1)<0.0 ) return v1 + v21*clamp( dot(p1,v21)/dot2(v21), 0.0, 1.0 );
    if( dot(cross(v32,nor),p2)<0.0 ) return v2 + v32*clamp( dot(p2,v32)/dot2(v32), 0.0, 1.0 );
    if( dot(cross(v03,nor),p3)<0.0 ) return v3 + v03*clamp( dot(p3,v03)/dot2(v03), 0.0, 1.0 );

    return p - nor*dot(nor,p-v0)/dot2(nor);
}

//==================================================================

// https://www.shadertoy.com/view/MlGcDz
float iTriangle( in vec3 ro, in vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2 )
{
    vec3 v1v0 = v1 - v0;
    vec3 v2v0 = v2 - v0;
    vec3 rov0 = ro - v0;

    vec3  n = cross( v1v0, v2v0 );
    vec3  q = cross( rov0, rd );
    float d = 1.0/dot( rd, n );
    float u = d*dot( -q, v2v0 );
    float v = d*dot(  q, v1v0 );
    float t = d*dot( -n, rov0 );

    if( u<0.0 || v<0.0 || (u+v)>1.0 ) t = -1.0;

    return t;
}

// https://www.shadertoy.com/view/4lcSRn
float iCylinder( in vec3 ro, in vec3 rd, 
                 in vec3 pa, in vec3 pb, float ra )
{
    vec3 ba = pb-pa;

    vec3  oc = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoc = dot(ba,oc);

    float k2 = baba            - bard*bard;
    float k1 = baba*dot(oc,rd) - baoc*bard;
    float k0 = baba*dot(oc,oc) - baoc*baoc - ra*ra*baba;

    float h = k1*k1 - k2*k0;
    if( h<0.0 ) return -1.0;
    h = sqrt(h);
    float t = (-k1-h)/k2;

    // body
    float y = baoc + t*bard;
    if( y>0.0 && y<baba ) return t;

    return -1.0;
}

float iSphere( in vec3 ro, in vec3 rd, in vec4 sph )
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return -1.0;
	return -b - sqrt( h );
}

float iPlane( in vec3 ro, in vec3 rd, in float h )
{
    return (h-ro.y)/rd.y;
}

vec3 nPlane( void )
{
    return vec3(0.0,1.0,0.0);
}

vec3 nTriangle( in vec3 v0, in vec3 v1, in vec3 v2 )
{
    vec3 v1v0 = v1 - v0;
    vec3 v2v0 = v2 - v0;
    return normalize( cross( v1v0, v2v0 ) );
}

vec3 nCylinder( in vec3 pos, in vec3 a, in vec3 b, in float r )
{
    vec3  ba = b - a;
    vec3  pa = pos - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    return (pa - h*ba)/r;
}

vec3 nSphere( in vec3 pos, in vec4 sph )
{
    return (pos-sph.xyz)/sph.w;
}

// https://www.shadertoy.com/view/3tBBW3
float oQuad( in vec3 pos, in vec3 nor, 
                     in vec3 v0, in vec3 v1,
                     in vec3 v2, in vec3 v3 ) 
{
    v0 = normalize(v0-pos);
    v1 = normalize(v1-pos);
    v2 = normalize(v2-pos);
    v3 = normalize(v3-pos);
    float k02 = dot( nor, normalize( cross(v3,v0)) ) * acos( dot(v0,v3) );
    float k23 = dot( nor, normalize( cross(v2,v3)) ) * acos( dot(v3,v2) );
    float k31 = dot( nor, normalize( cross(v1,v2)) ) * acos( dot(v2,v1) );
    float k10 = dot( nor, normalize( cross(v0,v1)) ) * acos( dot(v1,v0) );

    return abs(k02+k23+k31+k10)/6.283185;
}

// https://www.shadertoy.com/view/4djSDy
float oSphere( in vec3 pos, in vec3 nor, in vec4 sph )
{
    vec3  di = sph.xyz - pos;
    float l  = length(di);
    float nl = dot(nor,di/l);
    float h  = l/sph.w;
    float h2 = h*h;
    float k2 = 1.0 - h2*nl*nl;

    float res = max(0.0,nl)/h2;
    if( k2 > 0.0 ) 
        res = pow( clamp(0.5*(nl*h+1.0)/h2,0.0,1.0), 1.5 );

    return res;
}

//=====================================================

struct Scene
{
    vec3 v1;
	vec3 v2;
	vec3 v3;
    vec3 v4;
    vec3 pA;
    vec3 pB;
};

vec2 intersect( in vec3 ro, in vec3 rd, in Scene scn )
{
    vec2 res = vec2(1e10,-1.0);

    float t = iPlane(ro,rd,-1.5);
    if( t>0.0 ) res = vec2( t, 1.0 );

    t = iTriangle( ro, rd, scn.v1, scn.v2, scn.v4 );
    if( t>0.0 && t<res.x ) res = vec2( t, 2.0 );

    t = iTriangle( ro, rd, scn.v3, scn.v4, scn.v2 );
    if( t>0.0 && t<res.x ) res = vec2( t, 2.0 );

    t = iSphere( ro, rd, vec4(scn.pA,0.07) );
    if( t>0.0 && t<res.x ) res = vec2( t, 3.0 );

    t = iSphere( ro, rd, vec4(scn.pB,0.07) );
    if( t>0.0 && t<res.x ) res = vec2( t, 4.0 );

	t = iCylinder( ro, rd, scn.pA, scn.pB, 0.02 );
    if( t>0.0 && t<res.x ) res = vec2( t, 5.0 );

    return res;
}

vec3 calcNormal( in vec3 pos, in float objID, in Scene scn )
{
    if( objID<1.5 ) return nPlane();
    if( objID<2.5 ) return nTriangle( scn.v1, scn.v2, scn.v4 );
    if( objID<3.5 ) return nSphere( pos, vec4(scn.pA,0.07) );
    if( objID<4.5 ) return nSphere( pos, vec4(scn.pB,0.07) );
    if( objID<5.5 ) return nCylinder( pos, scn.pA, scn.pB, 0.02 );
    return vec3(0.0,1.0,0.0);
}

float calcOcclusion( in vec3 pos, in vec3 nor, in float objID, in Scene scn )
{
    float occ = 1.0;

    occ *= clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
    occ *= 1.0-oSphere( pos, nor, vec4(scn.pA,0.07) );
    occ *= 1.0-oSphere( pos, nor, vec4(scn.pB,0.07) );

    if( objID<1.5 ) occ *= 1.0-oQuad( pos, nor, scn.v1, scn.v2, scn.v3, scn.v4 );

    return occ;
}

const vec3 lig = normalize(vec3(1.0,0.9,0.7));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 tot = vec4(0.0);
#if AA>1
	#define ZERO min(iFrame,0)
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // 2 pixel wide triangular kernel
        vec2 o = 2.0*vec2(float(m),float(n)) / float(AA-1) - 1.0;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        float t = iTime - 0.5*(1.0/60.0)*float(m*AA+n)/float(AA*AA-1);
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        float t = iTime;
#endif

        // animate objects
        Scene scn;
        vec3 w = sin(0.2*t*vec3(1.0,1.1,1.2) + vec3(0.0,2.0,4.0) );
        w /= (w.x+w.y+w.z);
        scn.v1 = 1.5*vec3(1.0,0.4,1.0)*cos( 0.11*t + vec3(0.0,1.0,1.0) + 0.0 );
        scn.v2 = 1.5*vec3(1.0,0.4,1.0)*cos( 0.12*t + vec3(0.0,2.0,3.0) + 2.0 );
        scn.v3 = 1.5*vec3(1.0,0.4,1.0)*cos( 0.13*t + vec3(0.0,3.0,5.0) + 3.0 );
        scn.v4 = scn.v1 + (scn.v3 - scn.v2)*(0.55+0.45*sin( 0.37*t + 4.0 ));

        if( dot( cross( scn.v2-scn.v1, scn.v4-scn.v1 ), 
                 cross( scn.v4-scn.v3, scn.v2-scn.v3 )) < 0.0 )
        {
            vec3 tmp = scn.v3;
            scn.v3 = scn.v2;
            scn.v2 = tmp;
        }

        // compute closest point in quad
        scn.pA = vec3(cos(1.5*t+1.0),0.8+0.4*cos(t*1.1),cos(1.4*t+3.0));
        scn.pB = closestQuad( scn.v1, scn.v2, scn.v3, scn.v4, scn.pA );

        // render

        vec3 ro = vec3(0.0, 0.7, 3.5 );
        vec3 rd = normalize( vec3(p,-2.0) );
        rd.yz = (mat2(24,-7,7,24)/25.0)*rd.yz;

        vec3 col = vec3(0.0);

        vec2 tm = intersect( ro, rd, scn );
        if( tm.y>0.0 )
        {
            float t = tm.x;
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos, tm.y, scn);
            nor *= -sign(dot(nor,rd));
            float occ = calcOcclusion( pos, nor, tm.y, scn );
            col += vec3(1.4)*occ;

            vec3 mate = .55+0.45*cos(tm.y*3.5+vec3(0.0,1.0,1.5));

            // show distance isolines
            if( abs(tm.y-2.0)<0.5 )
            {
                float dref = length(scn.pA-closestQuad( scn.v1, scn.v2, scn.v3, scn.v4, scn.pA ));
                float dsam = length(pos-scn.pA);
                mate += 0.2*smoothstep(0.8,0.9,sin((dsam-dref)*75.0))*exp2(-15.0*(dsam-dref)*(dsam-dref));
            }
            col *= mate;

            col *= exp( -0.05*t );
        }

        col = pow( clamp(col,0.0,1.0), vec3(0.4545) );

        tot += vec4(col,1.0);
#if AA>1
    }
    tot.xyz /= tot.w;
#endif


    fragColor = vec4( tot.xyz, 1.0 );
}
