float slowSawStraight(float x){
    float result = min(1.0, mod(x,0.5)*6.0);
    if (mod(x,1.0) >= 0.5) result = 1.0-result;
    return result;
}
vec3 slowSawStraightThree(float x){
    return vec3(
        slowSawStraight(x+0.0),
        slowSawStraight(x+1.0/3.0),
        slowSawStraight(x+2.0/3.0)
    );
}

float slowSawSmooth(float x){
    float result = smoothstep(0.0,1.0, mod(x,0.5)*6.0);
    if (mod(x,1.0) >= 0.5) result = 1.0-result;
    return result;
}
vec3 slowSawSmoothThree(float x){
    return vec3(
        slowSawSmooth(x),
        slowSawSmooth(x+1.0/3.0),
        slowSawSmooth(x+2.0/3.0)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.xy;

    float hueVal = iTime*0.2+uv.x;
    vec3 col = slowSawSmoothThree(hueVal);
    if (uv.y < 0.2) col = slowSawStraightThree(hueVal);

    fragColor = vec4(col,1.0);
}
