// Cole Peterson

// Use the mouse to get new patterns! Lots of variation.

#define R iResolution.xy
#define m ((iMouse.xy - .5*R.xy) / R.y)

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage( out vec4 f, in vec2 u ){
    vec2 nuv = 1.5*vec2(u.xy - 0.5*R.xy)/R.y;
    nuv = (nuv + vec2(-nuv.y,nuv.x) ) / 1.4;
    nuv.y += iTime*.1;

    vec2 id = floor(nuv*1.);
    nuv = fract(nuv*1.)-.5;

    if(int(id.x) % 2 == 0) nuv.x *= -1.;
    if(int(id.y) % 2 == 0) nuv.y *= -1.;

    float n = 16., w = .31, q = -.605;
    float inc = 2.*3.1415 / n;

    if(iMouse.z > 0.){ 
        q += m.x * .3 + m.y*.4;
        w += m.y*.7;
    }

    for(float i = 0.; i < n; i++){
        vec2 of = .08*vec2(cos(i*.6), sin(i*.6));
        nuv += of;
        nuv *= rot(i * inc - 18.*.006 + w);
        nuv -= q;
        nuv *= 1.001;
        nuv = abs(nuv - of);
    }

    vec3 col = texture(iChannel0, nuv).xyz;
    col = pow(col*1.33, vec3(2.9));
    f = vec4(col, 1.);
}
