#define PI 3.1415926


float Cross(vec2 a, vec2 b)
{
    return a.x*b.y-a.y*b.x;
}

bool SameSide(vec2 A, vec2 B, vec2 C, vec2 P)
{
    vec2 AB = B-A;
    vec2 AC = C-A;
    vec2 AP = P-A;
    float c1 = Cross(AB,AC);
    float c2 = Cross(AB,AP);
    return c1*c2>=0.;
}

bool InTiger(vec2 A, vec2 B, vec2 C, vec2 P)
{
    return SameSide(A,B,C,P) &&
        SameSide(B,C,A,P) &&
        SameSide(C,A,B,P);
}

void DrawStar(vec2 uv, float rot, float r,inout vec4 color)
{
    //uv（0，1）-> (-1, 1)
    uv=2.*uv-vec2(1,1);
    // float r =0.5;
    float R = 0.98;
    float delta = 0.2*PI;
    float offset = 0.05*PI;
    for(int i=0;i<10;i++)
    {
        float v = float(i);
        float ang = delta*v+rot;
        vec2 c =vec2(R*cos(ang), R*sin(ang));
        float an1 = ang-offset ;
        float an2 = ang+offset ;
        vec2 c1 = vec2(r*cos(an1),r*sin(an1));
        vec2 c2 = vec2(r*cos(an2),r*sin(an2));
        if(InTiger(c,c1,c2,uv))
            color = vec4(1,0.6,.4,1);
    }
    if(length(uv)<r)
    {
        color=vec4(.92,.89,.41,1);
    }
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fragCoord/iResolution.xy;

    fragColor=vec4(.1,0,0.9,1.);

    DrawStar(uv,iTime, .5+.2*sin(iTime), fragColor);
}
