void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord / (iResolution.xy * 0.25);

    float p = bpm2ms(128.);
    float b0 = p * 0.002;
    float b1 = p * 0.00005;
    float b2 = p * 0.55 * zero2one(sin(iTime));

    float a0 = SinMod(iTime, b0, 0., 1.);
    float a1 = SinMod(iTime, b1, 0., 1.);
    float a2 = SinMod(iTime, b2, 0., 1.);

    float a = a0 * a1 * a2;
    float s = SinMod(iTime, a, 0., 1.);

    // visualise a2, the most prominent sin you can hear
    vec3 col = cos(a2 + uv.xyx + vec3(0,2,4));

    fragColor = vec4(col, 1.0);
}

float Amp(float a)
{
    return a * 0.1;
}

float SinWave(float f, float a, float t)
{
    return Amp(a) * sin(2. * PI * f * t);
}

vec2 mainSound(in int samp, float t)
{
    float p = bpm2ms(128.);
    float f = 440.;

    float b0 = p * 0.002;
    float b1 = p * 0.00005;
    float b2 = p * 0.55 * zero2one(sin(t));

    float a0 = SinMod(t, b0, 0., 1.);
    float a1 = SinMod(t, b1, 0., 1.);
    float a2 = SinMod(t, b2, 0., 1.);

    float vol = 0.3;
    float s = (a0 * a1 * a2) * SinWave(f, vol, t);

    return vec2(s);
}

#define PI 3.1415926536

float zero2one(float x)
{
    return (x - -1.) / (1. - -1.);
}

float bpm2ms(float bpm)
{
    float min_in_ms = 60000.0;
    float beat_ms = (min_in_ms / bpm) / 1000.0;
    return beat_ms;
}

float SinMod(float x, float b, float c, float d)
{
    float p = (2. * PI) / b;
    return sin(p * (x + c)) + d;
}
