float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
  vec3 ba = b - a; vec3 pa = p - a;
  vec3 cb = c - b; vec3 pb = p - b;
  vec3 ac = a - c; vec3 pc = p - c;
  vec3 nor = cross( ba, ac );

  return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) ) :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}



vec3 IniciarPiramideMedida(vec3 p, vec3 medida)
{

    vec3 res= vec3(9999.0, -1.0,-1.0);
    // face 1
    vec3 f1v1=vec3(1.0,-1.0,1.0);
    vec3 f1v2=vec3(-1.0,-1.0,-1.0);
    vec3 f1v3=vec3(1.0,-1.0,-1.0);

     // face 2
   vec3 f2v1=vec3(1.0,-1.0,-1.0);
   vec3 f2v2=vec3(0.0,1.0,0.0);
   vec3 f2v3=vec3(1.0,-1.0,1.0);


    // face 3
   vec3 f3v1=vec3(1.0,-1.0,1.0);
   vec3 f3v2=vec3(0.0,1.0,0.0);
   vec3 f3v3=vec3(-1.0,-1.0,1.0);


   // face 4
   vec3 f4v1=vec3(-1.0,-1.0,1.0);
   vec3 f4v2=vec3(0.0,1.0,0.0);
   vec3 f4v3=vec3(-1.0,-1.0,-1.0);

      // face 5
   vec3 f5v1=vec3(0.0,1.0,0.0);
   vec3 f5v2=vec3(1.0,-1.0,-1.0);
   vec3 f5v3=vec3(-1.0,-1.0,-1.0);


   // face 6
   vec3 f6v1=vec3(1.0,-1.0,1.0);
   vec3 f6v2=vec3(-1.0,-1.0,1.0);
   vec3 f6v3=vec3(-1.0,-1.0,-1.0);


   ///vec3 scala=vec3(10.0,30.0,10.0); 
   vec3 scala=medida;
   float udt1= udTriangle(  p/scala, f1v1, f1v2, f1v3 )*scala.x;
   float udt2= udTriangle(  p/scala, f2v1, f2v2, f2v3 )*scala.x;
   float udt3= udTriangle(  p/scala, f3v1, f3v2, f3v3 )*scala.x;
   float udt4= udTriangle(  p/scala, f4v1, f4v2, f4v3 )*scala.x;
   float udt5= udTriangle(  p/scala, f5v1, f5v2, f5v3 )*scala.x;
   float udt6= udTriangle(  p/scala, f6v1, f6v2, f6v3 )*scala.x;

   res =opU3(res, vec3(udt1,100.0,-1.0)); 
   res =opU3(res, vec3(udt2,100.0,-1.0)); 
   res =opU3(res, vec3(udt3,100.0,-1.0)); 
   res =opU3(res, vec3(udt4,100.0,-1.0)); 
   res =opU3(res, vec3(udt5,100.0,-1.0)); 
   res =opU3(res, vec3(udt6,100.0,-1.0)); 

   res.x=res.x-0.01;
   return res;

}

