vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

float sdVesica(vec2 p, float r, float d)
{
    p = abs(p);
    float b = sqrt(r*r-d*d);
    return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                             : length(p-vec2(-d,0.0))-r;
}

vec2 cuarto00(vec3 p)
{   vec3 p0=p;
    vec2 res= vec2(9999.0, -1.0); 
    float d1a= sdRoundBox( p-vec3(0.0,0.5,0.0), vec3(10.5,7.0,10.0), 0.5 );
    float d1aa= sdRoundBox( p-vec3(0.0,0.5,0.0), vec3(8.5,7.5,8.0), 0.5 );
    float d1b=sdVesica(p.xy, 4.0, 1.5);
    float d1c= sdBox(p-vec3(0.0,-2.0,0.0), vec3(2.4,3.0,11.0) );
    d1a=differenceSDF(d1a,d1aa);
    float d1bc= unionSDF(d1b, d1c);
    float ddif= differenceSDF(d1a, d1bc);

    float d3a= sdRoundBox( p-vec3(0.0,0.5,0.0), vec3(10.5,7.0,10.5), 0.5 );
    float d3aa= sdRoundBox( p-vec3(0.0,0.5,0.0), vec3(8.5,7.5,8.5), 0.5 );
    float d3b=sdVesica(p.xy, 4.5, 1.5);
    float d3c= sdBox(p-vec3(0.0,-2.0,0.0), vec3(3.5,3.0,11.5) );
    float d3bc= unionSDF(d3b, d3c);
    d3a=differenceSDF(d3a, d3aa);
    float ddif3= differenceSDF(d3a, d3bc);

    p.x=abs(p.x)-8.0;
    p.z=abs(p.z)-8.0;
    float d4a= sdCylinderXY( p-vec3(0.0,5.0,0.0), vec2(0.25,5.0) );

    p=p0;
    p.x=abs(p.x)-15.0;
    p.z=abs(p.z)-9.0;
    float d1=sdCylinderXZ( p, vec2(4.0,10.5) );
    float d2=sdCylinderXZ( p-vec3(0.0,10.5,0.0), vec2(5.0,1.0) );
    float d2a=sdCylinderXZ( p-vec3(0.0,10.5,0.0), vec2(4.0,1.5) );
    d2= differenceSDF(d2, d2a);
    //res =opU2(res, vec2(ddif,64.0));
    res =opU2(res, vec2(ddif,100.0));
    //res =opU2(res, vec2(ddif3,64.0));
    res =opU2(res, vec2(ddif3,101.0));

    res =opU2(res, vec2(d1,64.0));
    res =opU2(res, vec2(d2,64.0));
    res =opU2(res, vec2(d4a,2.0));
    return res;
}   

vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0); 
     p.y=p.y-6.0;
    vec3 p0=p;
	float planeDist1 = p.y+5.5;  //piso inf
    res =opU2(res, vec2(planeDist1,100.0));
    p.z= opRep1D(p.z, 35.0 );
    vec2 re1= cuarto00(p);
    res =opU2(res, re1);
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.1;
    float tmax = 40.0;
    vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
   for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*object.x;
        hit = GetDist(p);

        object.x += hit.x;
        object.y = hit.y;
        if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }
    return object;
}

float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 1.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
        float hit = GetDist(p + lightPos * dist).x;
        res = min(res, hit / (dist * lightSize));
        dist += hit;
        if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {
        float hr = 0.01 + float(i) * 0.5 / 4.0;
        float dd = GetDist(nor * hr + pos).x;
        occ += (hr - dd)*sca;
        sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 color, float t) 
{   vec3 lightPos=lp;
    vec3 hit = ro + rd * t;
    vec3 norm = GetNormal(hit);
    vec3 light = lightPos - hit;
    float lightDist = max(length(light), .001);
    float atten = 1. / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    light /= lightDist;

    vec3 R = reflect(-light, norm);
    vec3 V = -rd;
    // shadows
    float shadow = getSoftShadow(hit, normalize(lightPos));
    // occ
    float occ = occlusion(hit, norm);

    vec3 ambient= 0.5*color*clamp(0.5 + 0.5*norm.y, 0.0, 1.0);
    vec3 fresnel =  color *  pow(clamp(1.0 + dot(rd, norm), 0.0, 1.0), 2.0);;
    float dif = clamp(dot(norm, light), 0.0, 1.0);
    dif = pow(dif, 4.) * 2.;
    vec3 diffuse =  color * dif;

    float specular = pow(max(dot(R, V), 0.), 8.);
     // back
    vec3 back = 0.5 * color * clamp(dot(norm, -light), 0.0, 1.0);
       vec3 colOut = occ*color*(ambient+diffuse*shadow+.35 +back) + vec3(.7,.9,1)*specular*specular;
    return colOut;
}

