void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
    fragColor.yz = 0.5 * (fragColor.yz + vec2(1, 1)) * fragColor.x;
}

#define N 4
#define WF 0.6
#define DISPERSION 1.6

vec2 norm(vec2 v)
{
    if(length(v) <= 0.000001)
        return v;
    return normalize(v);
}

float rand(vec2 co)
{
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 mouse = iMouse / iResolution.x;

    vec2 uv = fragCoord/iResolution.xy;
    if(iFrame <= 10)
    {
        fragColor = texture(iChannel1, uv) - vec4(0., 0.5, 0.5, 0.);
        return;
    }
    if(mouse.w > 0. && distance(fragCoord, iMouse.xy) < 10.)
    {
        fragColor = vec4(1., rand(vec2(iTime, 42.) + mouse.xy) * 2. - 1., rand(vec2(iTime, 43.) + mouse.xy) * 2. - 1., 1.);
        return;
    }
    ivec2 pos = ivec2(fragCoord);

    vec2 finalVel;
    float finalAmount = 0.;
    for(int dx = -1; dx <= 1; dx++)
    {
        for(int dy = -1; dy <= 1; dy++)
        {
            if(abs(dx) + abs(dy) != 1)
                continue;
            vec4 cur = texelFetch(iChannel0, pos + ivec2(-dx, -dy), 0);
            vec2 cVel = cur.yz;
            float cAmount = cur.x;
            float mulSum = abs(cVel.x) + abs(cVel.y) + 0.000000001 + max(0., cAmount - 1.);
            float mul = max(0., dot(vec2(dx, dy), cVel)) + max(0., cAmount - 1.) / 4.; // 4 = number of cardinal directions
            mul /= mulSum;
            cVel += max(0., cAmount - 1.) * vec2(dx, dy) * DISPERSION;
            finalVel += cVel * cAmount * mul;
            finalAmount += cAmount * mul;
        }
    }
    finalVel = norm(finalVel);

    vec2 alignVel;
    vec2 cohesVel;
    vec2 avoidVel;
    for(int dx = -N; dx <= N; dx++)
    {
        for(int dy = -N; dy <= N; dy++)
        {
            vec4 cur = texelFetch(iChannel0, pos + ivec2(dx, dy), 0);
            vec2 cVel = cur.yz;
            float cAmount = cur.x;
            alignVel += cVel * cAmount;
            cohesVel += norm(vec2(dx, dy)) * cAmount;
            if(abs(dx) + abs(dy) <= 3)
                avoidVel += norm(vec2(dx, dy)) * (1. / (cAmount * cAmount * cAmount + 1.));
        }
    }
    vec2 wallVel = 
        vec2(1. / (WF + uv.x), 0.) +
        vec2(0., 1. / (WF + uv.y)) +
        vec2(0., -1. / (1. - uv.y + WF)) +
        vec2(-1. / (1. - uv.x + WF), 0.);
    vec2 randVel = norm(vec2(rand(vec2(pos) + vec2(iTime, 1.)), rand(vec2(pos) + vec2(iTime, 0.))) * 2. - vec2(1, 1));
    alignVel = norm(alignVel);
    cohesVel = norm(cohesVel);
    vec2 rotationVel = vec2(finalVel.y, - finalVel.x);
    finalVel = norm(
        2.0 * finalVel + 
        0.3 * alignVel + 
        0.1 * cohesVel + 
        0.0 * avoidVel + 
        0.1 * wallVel + 
        0.2 * randVel + 
        0.0 * rotationVel
    );

    fragColor = vec4(finalAmount, finalVel, 1.);
}
