 	light_pos2= vec3(10.0, 20.0, -25.0 ); light_color2 =vec3( 1.0,0.0,1.0 ); 
   //vec3 ro=vec3(0.0,7.0,-25.0);

   float py=20.0*sin(t*2.0);
