mat2 rot(float angle) {
	float s = sin(angle);
	float c = cos(angle);

	return mat2(
		c, -s,
		s, c
	);
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float scale = 10.;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;
    vec2 orig = (uv + vec2(sin(iTime), cos(iTime)) * 0.4);
    vec2 cam = orig * rot(iTime / 2.) * scale;
    vec2 guv = fract(cam);
    vec2 gid = floor(cam);

    vec3 col = vec3(0);

    float minn = 0.1;
    float maxx = 0.43;
    float border = (sin(iTime - length(gid)) + 1.) / 2. * (maxx - minn) + minn;
    float ripples = 1.;
    float intense = abs(ripples / 2. - mod(abs(length(guv - 0.5) - iTime * 0.2 + rand(gid)), ripples)) / ripples;
    col = vec3(smoothstep(0., .3, intense) * smoothstep(border + .1, border, length(guv - .5)));


    // Time varying pixel color
    col *= 0.5 +rand(vec2(floor(iTime * 2.))+gid) / 2. * cos(gid.xyx+vec3(0,2,4) + vec3(floor(iTime * 2.)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
