// More balls leaping downstairs
// A companion to "Stairway from Heaven" (and based on "Racetrack")

#define AA  0   // (= 0/1) optional antialiasing

#define VAR_ZERO min (iFrame, 0)

float PrCylDf (vec3 p, float r, float h);
mat3 QtToRMat (vec4 q);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
vec3 HsvToRgb (vec3 c);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

const int nBall = 200;   // same in all shaders
const float nLev = 16.;

vec3 ltDir, vnBall, bSize;
float tCur, dstFar, helHt;
int idBall, idObj;
const int idHel = 1, idTier = 2, idWal = 3, idGrnd = 4;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, h, r;
  dMin = dstFar;
  q = p;
  q.y -= helHt;
  d = PrCylDf (q.xzy, bSize.x, 0.01);
  DMIN (idHel);
  q = p;
  q.y -= 0.5;
  d = dMin;
  for (float nh = float (VAR_ZERO); nh < nLev; nh ++) {
    q.y -= bSize.y;
    d = min (d, PrCylDf (q.xzy, bSize.x - 0.5 + nh * bSize.z, bSize.y));
  }
  q = p;
  h = 0.5 * (nLev * bSize.y + 0.5);
  q.y -= h;
  r = bSize.x + nLev * bSize.z;
  d = max (PrCylDf (q.xzy, r - 0.25, h), - d);
  DMIN (idTier);
  h += 1.3 * bSize.y;
  q = p;
  q.y -= h;
  d = max (PrCylDf (q.xzy, r + 0.5, h), r - 0.5 - length (q.xz));
  DMIN (idWal);
  q = p;
  d = max (q.y - 2. * h + 1., r - length (q.xz));
  DMIN (idGrnd);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.47 * p.w * clamp ((nLev + 2.) * bSize.y + mod (float (n), 2.) - p.y, 0.1, 1.);
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnBall = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
}

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 rs, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.47 * p.w * clamp ((nLev + 2.) * bSize.y + mod (float (n), 2.) - p.y, 0.1, 1.);
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return 0.5 + 0.5 * smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  vec2 a;
  u = vnBall * QtToRMat (Loadv4 (4 * idBall + 2));
  a = mod (floor (8. * vec2 (atan (u.x, u.y), asin (u.z)) / pi), 2.) - 0.5;
  return 0.6 + 0.4 * step (0., sign (a.x) * sign (a.y));
}

vec3 BallCol ()
{
  float c;
  c = 33. * float (idBall) / float (nBall);
  return HsvToRgb (vec3 (mod (c, 1.), 1. - 0.1 * mod (c, 8.), 1.)) * BallChqr (idBall, vnBall);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  vec2 vf;
  float dstObj, dstBall, sh;
  col = vec3 (0.2);
  dstObj = ObjRay (ro, rd);
  dstBall = BallHit (ro, rd);
  if (min (dstBall, dstObj) < dstFar) {
    if (dstObj < dstBall) {
      ro += rd * dstObj;
      vn = ObjNf (ro);
      if (idObj == idHel) {
        col4 = vec4 (mix (vec3 (1., 0., 0.), vec3 (0.8, 0.4, 0.),
           step (0.6, Fbm2 (4. * ro.xz * vec2 (1. + 0.5 * sin (tCur))))), -1.);
        vf = vec2 (4., 2.);
      } else if (idObj == idWal) {
        col4 = vec4 (0.6, 0.6, 0.65, 0.05) * (0.85 + 0.15 * smoothstep (0., 0.05, mod (0.5 * ro.y, 1.)));
        vf = vec2 (32., 1.);
      } else if (idObj == idTier) {
        col4 = (floor (mod (ro.y / bSize.y + 0.55, 2.)) == 0.) ? vec4 (0.5, 0.4, 0.3, 0.05) :
           vec4 (0.6, 0.65, 0.5, 0.05);
        vf = vec2 (32., 0.5);
      } else if (idObj == idGrnd) {
        col4 = mix (vec4 (0.2, 0.25, 0.2, 0.), vec4 (0.4, 0.4, 0.2, 0.), Noisefv2 (128. * ro.xz)) *
           (1. - 0.3 * Fbm2 (0.25 * ro.zx));
        vf = vec2 (4., 2.);
      }
      vn = VaryNf (vf.x * ro, vn, vf.y);
    } else {
      ro += dstBall * rd;
      vn = vnBall;
      col4 = vec4 (BallCol (), 0.1);
    }
    if (col4.a >= 0.) {
      sh = min (ObjSShadow (ro + 0.01 * ltDir, ltDir), BallHitSh (ro + 0.01 * ltDir, ltDir, 10.));
      col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    } else col = col4.rgb * (0.6 - 0.4 * dot (vn, rd));
    if (dstBall < dstObj) col = mix (vec3 (1., 0., 0.) * (0.6 - 0.4 * dot (vn, rd)), col, 
       0.3 + 0.7 * smoothstep (0.2, 1.2, Loadv4 (4 * idBall).y - helHt));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, stDat;
  vec3 rd, ro, vd, col;
  vec2 canvas, uv;
  float az, el, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  bSize = Loadv4 (4 * nBall).xyz;
  helHt = 0.5 + 0.4 * (1. + sin (0.2 * pi * tCur));
  dstFar = 200.;
  az = 0.;
  el = -0.3 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= 0.2 * pi * mPtr.y;
  }
  el = clamp (el, -0.35 * pi, -0.22 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 7., -110.);
  ltDir = vuMat * normalize (vec3 (1., 2., -1.));
  zmFac = 4.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.71 / canvas.y, 0.),
       0.5 * pi * (a + 0.5)), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (clamp (col, 0., 1.), 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

// "Stairway to Hell" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define VAR_ZERO min (iFrame, 0)

vec4 QtMul (vec4 q1, vec4 q2);
mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
vec4 EulToQt (vec3 e);
mat3 LpStepMat (vec3 a);
float Hashff (float p);
float Noisefv2 (vec2 p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const int nBall = 200;
const float nLev = 16.;

vec3 bSize;
float todCur, stSize, fOvlap, fricN, fricS, fricSW, fricT;
const float pi = 3.14159;

void CollFc (float rSep, float szAv, vec3 dr, vec3 dv, vec3 dw, inout vec3 am, inout vec3 wam)
{
  float fc, drv, ft;
  fc = fOvlap * (szAv / rSep - 1.);
  drv = dot (dr, dv) / (rSep * rSep);
  fc = max (fc - fricN * drv, 0.);
  am += fc * dr;
  dv -= drv * dr + cross (dw, dr);
  ft = min (fricT, fricSW * abs (fc) * rSep / max (0.001, length (dv)));
  am -= ft * dv;
  wam += (ft / rSep) * cross (dr, dv);
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm, out float sz)
{
  vec4 p;
  vec3 rmN, vmN, wmN, dr, am, wam;
  float fDamp, fCent, grav, rSep, szN, szAv, ft, ms, drv, dt, fnh;
  fOvlap = 1000.;
  fricN = 10.;
  fricS = 0.05;
  fricSW = 1.;
  fricT = 0.5;
  fDamp = 0.02;
  fCent = 0.01;
  grav = 10.;
  p = Loadv4 (4 * mId);
  rm = p.xyz;
  sz = p.w;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  ms = sz * sz * sz;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    rmN = p.xyz;
    szN = p.w;
    dr = rm - rmN;
    rSep = length (dr);
    szAv = 0.5 * (sz + szN);
    if (n != mId && rSep < szAv) {
      vmN = Loadv4 (4 * n + 1).xyz;
      wmN = Loadv4 (4 * n + 3).xyz;
      CollFc (rSep, szAv, dr, vm - vmN, (sz * wm + szN * wmN) / (sz + szN), am, wam);
    }
  }
  for (float nh = float (VAR_ZERO); nh <= nLev; nh ++) {
    dr.y = (nh < nLev) ? max (abs (rm.y) - (nh + 1.) * bSize.y, 0.) : 0.;
    dr.xz = vec2 (min (length (rm.xz) - bSize.x - nh * bSize.z, 0.) * normalize (rm.xz));
    rSep = length (dr);
    if (rSep < szAv) {
      CollFc (rSep, szAv, dr, vm, wm, am, wam);
    }
  }
  am.xz -= fCent * normalize (rm.xz);
  am.y -= grav * ms;
  am -= fDamp * vm;
  dt = 0.01;
  vm += dt * am / ms;
  rm += dt * vm;
  wm += dt * wam / (0.1 * ms * sz);
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm, out float sz)
{
  vec3 e;
  float mIdf;
  mIdf = float (mId);
  rm.xz = (bSize.x + (nLev - 1.) * bSize.z) * sin (2. * pi * mIdf / float (nBall) + vec2 (0.5 * pi, 0.));
  rm.y = (nLev + 2.) * bSize.y + mod (mIdf, 2.);
  vm = 0.5 * normalize (vec3 (Hashff (mIdf + todCur), 0., Hashff (mIdf + todCur)) - 0.5);
  e = normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6)));
  qm = EulToQt (e);
  wm = vec3 (0.);
  sz = 1. - 0.2 * Hashff (mIdf + 0.1);
}

const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat, qm;
  vec3 rm, vm, wm;
  vec2 iFrag;
  float sz, nStep;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBall + 1) discard;
  todCur = iDate.w;
  doInit = (iFrame <= 5);
  if (doInit) {
    bSize = vec3 (3., 1., 1.6);
    nStep = 0.;
  } else {
    stDat = Loadv4 (4 * nBall);
    bSize = stDat.xyz;
    nStep = stDat.w;
    ++ nStep;
  }
  mId = (pxId < 4 * nBall) ? pxId / 4 : -1;
  if (mId >= 0) {
    if (doInit) Init (mId, rm, vm, qm, wm, sz);
    else {
      Step (mId, rm, vm, qm, wm, sz);
      if (rm.y < 0.) Init (mId, rm, vm, qm, wm, sz);
    }
  }
  if (pxId < 4 * nBall) {
    kp = pxId - 4 * mId;
    if      (kp == 0) stDat = vec4 (rm, sz);
    else if (kp == 1) stDat = vec4 (vm, 0.);
    else if (kp == 2) stDat = qm;
    else if (kp == 3) stDat = vec4 (wm, 0.);
  } else {
    stDat = vec4 (bSize, nStep);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 EulToQt (vec3 e)
{
  float a1, a2, a3, c1, s1;
  a1 = 0.5 * e.y;  a2 = 0.5 * (e.x - e.z);  a3 = 0.5 * (e.x + e.z);
  s1 = sin (a1);  c1 = cos (a1);
  return normalize (vec4 (s1 * cos (a2), s1 * sin (a2), c1 * sin (a3),
     c1 * cos (a3)));
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

