float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}


float sinline(in vec2 p, float amp, float freq)
{
    float s0 = 1.0;
    float c0 = 0.0;
    float s1 = 1.0;
    float c1 = 0.0;
    float sd = 1e10;

    float x = 0.0;
    const float dx = 0.3;
    for(int i = 0; i < 128; i++)
    {
        //integrate 
        c1 = c0 - s0*dx;
        s1 = s0 + c1*dx;

        //plot line
        sd = min(sd, sdSegment(p, vec2(x*freq, s0*amp), vec2((x+dx)*freq, s1*amp)));

        x += dx;
        c0 = c1; s0 = s1;
    }

    return sd;
}

void mainImage( out vec4 C, in vec2 X )
{
    float sd = sinline(X - iResolution.xy*vec2(0.0,0.5), 30.0, 30.);

    vec3 col = (sin(sd/4.0)*0.5 + 0.5)/(1.0 + 0.1*sd*vec3(0.792,0.3,0.098));
    // Output to screen
    C = vec4(col,1.0);
}
