#define TWO_PI 6.28318530718
#define PI 3.14159265359

float noise(vec2 p) {
    return texture(iChannel0, p * 0.05 ).x;
}

float fbm(vec2 p) {
    float a =1.;
    float f = 1.;
    return a*noise(p) 
        + a*0.5 * noise(p*f*2. ) 
        + a*0.25 * noise(p*f*4. )
        + a*0.1 * noise(p*f*8. );
}

float circle(vec2 p) {
    float r = length(p);
    float radius = 0.4;
    float height = 1.; 
    float width = 150.;

	return height - pow(r - radius, 2.) *width ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = ( fragCoord -.5 * iResolution.xy ) / iResolution.y;

    vec2 st  = vec2(
            atan(uv.y, uv.x) ,
            length(uv) * 1. + iTime * 0.1
        );


    st.x += st.y * 1.1;// - iTime * 0.3;
    st.x = mod(st.x , TWO_PI);


    float n = fbm(st ) * 1.5 -1. ;
    n = max(n, 0.1);
    float circle =  max(1.- circle(uv ), 0.)  ;

    float color = n/circle;
    float mask = smoothstep(0.48, 0.4, length(uv));

    color *= mask;
    vec3 rez = vec3(1., 0.5, 0.25) * color;
    // Output to screen
    fragColor = vec4(rez,1.0);
}
