float circle(in vec2 _st, in float _radius){
    if(_st.y<0.5) {
        return 0.;
    }
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*3.360);
}

float ring(in vec2 _st, in float _radius) {
    return circle(_st,_radius)- circle(_st,_radius*0.8);
}

vec3 Rainbow(vec2 st) {
    st = fract(st);
	float r = 0.28;
	float c1 = ring(st,r);
    float c2 = ring(st,r*0.6);
    float c3 = ring(st,r*0.6*0.6);
    vec3 color = vec3(0.);
    color += mix(color, vec3(1.000,0.185,0.499),c2);
    color += mix(color, vec3(0.345,0.095,1.000),c3);
	color += mix(color, vec3(1.000,0.331,0.245),c1);
    return color;
}

vec2 movingTiles(vec2 _st, float _zoom, float _speed){
    _st *= _zoom;
    float time = iTime*_speed;
    if (fract( _st.y * 0.5) > 0.5){
            _st.x += fract(time)*2.0;
        } else {
            _st.x -= fract(time)*2.0;
        }
    return fract(_st);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord-.5*iResolution.xy)/iResolution.y;

    st = movingTiles(st,4.,0.1);
    vec3 col =vec3(0.);
    vec3 rainbow = Rainbow(st);
	col = rainbow;

    fragColor = vec4(col,1.0);
}
